INSERT INTO `blogs` (`blog_id`, `blog_name`, `blog_content`, `blog_time`, `blog_cat_ids`, `blog_image`, `blog_seo_title`, `blog_seo_keyword`, `blog_seo_description`, `blog_user_id`, `blog_views`) VALUES
(107, 'Mẫu đăng ký phương pháp khấu hao tài sản cố định', '<div class="name4" xss=removed><em xss=removed>Trước khi doanh nghiệp bắt đầu trích khấu hao tài sản cố định, cần phải làm đăng ký phương pháp trích khấu hao TSCĐ đã lựa chọn với cơ quan thuế quản lý trực tiếp.</em></div>\r\n<div class="contentNew" xss=removed>\r\n<p xss=removed><span xss=removed><span xss=removed><span xss=removed>Phương pháp khấu hao áp dụng cho từng TSCĐ mà doanh nghiệp đã lựa chọn và đăng ký phải được thực hiện nhất quán trong suốt quá trình sử dụng TSCĐ trừ khi có sự thay đổi trong cách thức sử dụng tài sản đó để đem lại lợi ích cho doanh nghiệp hoặc khi có thay đổi đáng kể cách thức ước tính thu hồi lợi ích kinh tế cho doanh nghiệp.</span></span></span></p>\r\n<p xss=removed><span xss=removed><span xss=removed><span xss=removed>Trường hợp đặc biệt cần thay đổi phương pháp khấu hao, doanh nghiệp phải giải trình rõ sự thay đổi về cách thức sử dụng TSCĐ và thay đổi về cách thức ước tính thu hồi lợi ích kinh tế cho doanh nghiệp đối với TSCĐ cần thay đổi phương pháp khấu hao và mỗi tài sản cố định chỉ được phép thay đổi phương pháp trích khấu hao </span></span></span><strong><span xss=removed><span xss=removed><span xss=removed>tối đa không quá hai lần</span></span></span></strong><span xss=removed><span xss=removed><span xss=removed> trong quá trình sử dụng và phải có ý kiến bằng văn bản của cơ quan thuế quản lý trực tiếp.</span></span></span></p>\r\n<p xss=removed><strong><span xss=removed> Dưới đây là mẫu đăng ký phương pháp khấu hao TSCĐ:</span></strong><br> </p>\r\n<table border="1" xss=removed cellspacing="1" cellpadding="1">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM<br>Độc lập - Tự do - Hạnh phúc</strong></span></span></span></p>\r\n<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>ĐĂNG KÝ PHƯƠNG PHÁP KHẤU HAO TÀI SẢN CỐ ĐỊNH</strong></span></span></span></h2>\r\n<p xss=removed><span xss=removed><span xss=removed><span xss=removed><em><span xss=removed>Kính gởi</span></em> :       <strong>- CHI CỤC THUẾ QUẬN ...................<br>                      - ĐỘI QUẢN LÝ DOANH NGHIỆP.</strong></span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>-         Tên đơn vị                 :</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>-         Địa chỉ trụ sở chính  :</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>-         Địa chỉ chi nhánh      :</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>-         Mã số thuế                 :</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Thực hiện theo quy định tại Điều 13, Khoản 3 Thông tư 45/2013/TT-BTC ngày 25/14/2013 của Bộ Tài Chính về ban hành chế độ quản lý, sử dụng và trích khấu hao tài sản cố định.</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Nay Công ty .................. đăng ký phương pháp trích khấu hao tài sản cố định với Chi Cục thuế Quận …………theo phương pháp khấu hao ………</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Xin chân thành cảm ơn</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Chào trân trọng!       </span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed>                                                                            <em>Tp.HCM, ngày        tháng      năm 2016</em></span></span></span></p>\r\n<p xss=removed><span xss=removed><span xss=removed><span xss=removed>                                                       <strong>CÔNG TY ...............<br>                                                                       </strong></span></span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', 1488635767, '["5"]', 'http://ketoanbanthoigian.com/images/tram/ketoan-7.jpg', 'Mẫu đăng ký phương pháp khấu hao tài sản cố định', 'Mẫu đăng ký phương pháp khấu hao tài sản cố định', 'Mẫu đăng ký phương pháp khấu hao tài sản cố địnhTrước khi doanh nghiệp bắt đầu trích khấu hao tài sản cố định, cần phải làm đăng ký phương pháp trích khấu hao TSCĐ đã l', 14, 0),
(108, 'Cách kê khai chuyển nhượng vốn - Bán toàn bộ công ty - DNTN', '<p>Khai thuế thu nhập doanh nghiệp đối với hoạt động chuyển nhượng vốn <br>- Trình tự thực hiện:</p>\r\n<p>Bước 1. Doanh nghiệp có thu nhập từ chuyển nhượng vốn có trách nhiệm xác định, kê khai số thuế thu nhập doanh nghiệp từ chuyển nhượng vốn vào tờ khai quyết toán theo năm.<br>  Trường hợp (1) bán toàn bộ Công ty trách nhiệm hữu hạn một thành viên do tổ chức làm chủ sở hữu dưới hình thức chuyển nhượng vốn có gắn với bất động sản thì lập hồ sơ và gửi đến cơ quan thuế, chậm nhất là ngày thứ 10 (mười), kể từ ngày phát sinh nghĩa vụ thuế.<br> Trường hợp (2) Tổ chức nước ngoài kinh doanh tại Việt Nam hoặc có thu nhập tại Việt Nam (gọi chung là nhà thầu nước ngoài) mà tổ chức này không hoạt động theo Luật Đầu tư, Luật Doanh nghiệp có hoạt động chuyển nhượng vốn thì khai thuế thu nhập doanh nghiệp theo từng lần phát sinh.<br> Tổ chức, cá nhân nhận chuyển nhượng vốn có trách nhiệm xác định, kê khai, khấu trừ và nộp thay tổ chức nước ngoài số thuế thu nhập doanh nghiệp phải nộp. Trường hợp bên nhận chuyển nhượng vốn cũng là tổ chức nước ngoài không hoạt động theo Luật Đầu tư, Luật Doanh nghiệp thì doanh nghiệp thành lập theo pháp luật Việt Nam nơi các tổ chức nước ngoài đầu tư vốn có trách nhiệm kê khai và nộp thay số thuế thu nhập doanh nghiệp phải nộp từ hoạt động chuyển nhượng vốn của tổ chức nước ngoài.<br>Thời hạn nộp hồ sơ khai thuế là ngày thứ 10 (mười) kể từ ngày cơ quan có thẩm quyền chuẩn y việc chuyển nhượng vốn, hoặc ngày thứ 10 (mười) kể từ ngày các bên thỏa thuận chuyển nhượng vốn tại hợp đồng chuyển nhượng vốn đối với trường hợp không phải chuẩn y việc chuyển nhượng vốn.<br> <br>Bước 2. Cơ quan thuế thực hiện tiếp nhận:<br>  Trường hợp hồ sơ được nộp trực tiếp tại cơ quan thuế, công chức thuế tiếp nhận và đóng dấu tiếp nhận hồ sơ, ghi thời gian nhận hồ sơ, ghi nhận số lượng tài liệu trong hồ sơ và ghi vào sổ văn thư của cơ quan thuế.<br>  Trường hợp hồ sơ được gửi qua đường bưu chính, công chức thuế đóng dấu ghi ngày nhận hồ sơ và ghi vào sổ văn thư của cơ quan thuế.<br>  Trường hợp hồ sơ được nộp thông qua giao dịch điện tử, việc tiếp nhận, kiểm tra, chấp nhận hồ sơ khai thuế do cơ quan thuế thực hiện thông qua hệ thống xử lý dữ liệu điện tử.<br>- Cách thức thực hiện:<br>  Gửi trực tiếp tại trụ sở cơ quan thuế.<br>  Hoặc gửi qua hệ thống bưu chính.<br>  Hoặc gửi bằng văn bản điện tử qua cổng thông tin điện tử của cơ quan thuế.</p>\r\n<p>- Thành phần, số lượng hồ sơ:<br>a) Thành phần hồ sơ:<br>i) Trường hợp (1): hồ sơ gồm: Tờ khai thuế thu nhập doanh nghiệp theo Mẫu số 06/TNDN ban hành kèm theo Thông tư số 151/2014/TT-BTC.<br>ii) Trường hợp (2): hồ sơ gồm:<br>  Tờ khai thuế thu nhập doanh nghiệp theo Mẫu số 05/TNDN ban hành kèm theo Thông tư số 156/2013/TT-BTC;<br>  Bản chụp hợp đồng chuyển nhượng. Trường hợp hợp đồng chuyển nhượng bằng tiếng nước ngoài phải dịch ra tiếng Việt các nội dung chủ yếu: Bên chuyển nhượng; bên nhận chuyển nhượng; thời gian chuyển nhượng; nội dung chuyển nhượng; quyền và nghĩa vụ của từng bên; giá trị của hợp đồng; thời hạn, phương thức, đồng tiền thanh toán. <br>  Bản chụp quyết định chuẩn y việc chuyển nhượng vốn của cơ quan có thẩm quyền (nếu có);<br>  Bản chụp chứng nhận vốn góp;<br>  Chứng từ gốc của các khoản chi phí. <br>b) Số lượng hồ sơ: 01 bộ.<br>- Thời hạn giải quyết : Không phải trả kết quả cho người nộp thuế.<br>- Đối tượng thực hiện thủ tục hành chính: Doanh nghiệp, tổ chức, cá nhân.<br>- Cơ quan thực hiện thủ tục hành chính: Cục thuế<br>- Kết quả thực hiện thủ tục hành chính: Hồ sơ gửi đến cơ quan thuế không có kết quả giải quyết.<br>- Lệ phí (nếu có): Không có<br>- Tên mẫu đơn, tờ khai (nếu có): <br>  Tờ khai thuế thu nhập doanh nghiệp theo Mẫu số 06/TNDN ban hành kèm theo Thông tư số 151/2014/TT-BTC đối với trường hợp (1);<br>  Tờ khai thuế thu nhập doanh nghiệp theo Mẫu số 05/TNDN ban hành kèm theo Thông tư số 156/2013/TT-BTC đối với trường hợp (2).<br>- Yêu cầu, điều kiện thực hiện thủ tục hành chính (nếu có): trường hợp người nộp thuế lựa chọn và gửi hồ sơ đến cơ quan thuế thông qua giao dịch điện tử thì phải tuân thủ đúng các quy định của pháp luật về giao dịch điện tử.<br>- Căn cứ pháp lý của thủ tục hành chính:<br>  Luật Quản lý thuế số 78/2006/QH11 ngày 29 tháng 11 năm 2006;<br>  Luật sửa đổi, bổ sung một số điều của Luật Quản lý thuế số 21/2012/QH12 ngày 20 tháng 11 năm 2012;<br>  Nghị định số 83/2013/NĐ-CP ngày 22 tháng 7 năm 2013 của Chính phủ quy định chi tiết thi hành một số điều của Luật Quản lý thuế và Luật sửa đổi, bổ sung một số điều của Luật Quản lý thuế;<br>  Nghị định số 91/2014/NĐ-CP ngày 01 tháng 10 năm 2014 của Chính phủ về việc sửa đổi, bổ sung một số điều tại các Nghị định quy định về thuế;<br>  Thông tư số 156/2013/TT-BTC ngày 06/11/2013 của Bộ Tài chính hướng dẫn thi hành một số điều của Luật Quản lý thuế; Luật sửa đổi, bổ sung một số điều của Luật Quản lý thuế và Nghị định số 83/2013/NĐ-CP ngày 22 tháng 7 năm 2013 của Chính phủ;<br>  Điều 16, Thông tư số 151/2014/TT-BTC ngày 10/10/2014của Bộ Tài chính hướng dẫn thi hành Nghị định số 91/2014/NĐ-CP ngày 01/10/2014 của Chính phủ về việc sửa đổi, bổ sung một số điều tại các Nghị định quy định về thuế.<br>  Thông tư số 180/2010/TT-BTC ngày 10/11/2010 của Bộ Tài chính hướng dẫn giao dịch điện tử trong lĩnh vực thuế.</p>', 1488635769, '["5"]', 'http://ketoanbanthoigian.com/images/hinh-anh/Dich_vu_ke_toan_tai_nha3.jpg', 'Cách kê khai chuyển nhượng vốn - Bán toàn bộ công ty - DNTN', 'Cách kê khai chuyển nhượng vốn - Bán toàn bộ công ty - DNTN', 'Cách kê khai chuyển nhượng vốn - Bán toàn bộ công ty - DNTNKhai thuế thu nhập doanh nghiệp đối với hoạt động chuyển nhượng vốn - Trình tự thực hiện:\r\nBước 1. Doanh nghiệp có thu nhậ', 14, 0),
(109, 'Xử phạt đối với hành vi khai không đầy đủ các nội dung trong hồ sơ thuế', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Tại Điều 8 của thông thư 166/2013/TT-BTC có ghi các mức xử phạt đối với hành vi khai không đầy đủ các nội dung trong hồ sơ thuế như sau:</span></span></span></h2>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Phạt tiền 700.000 đồng, nếu có tình tiết giảm nhẹ thì mức tiền phạt tối thiểu không thấp hơn 400.000 đồng hoặc có tình tiết tăng nặng thì mức tiền phạt tối đa không quá 1.000.000 đồng đối với hành vi lập hồ sơ khai thuế ghi thiếu, ghi sai các chỉ tiêu làm căn cứ xác định nghĩa vụ thuế trên bảng kê hóa đơn, hàng hoá, dịch vụ mua vào, bán ra hoặc trên các tài liệu khác liên quan đến nghĩa vụ thuế.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Phạt tiền 1.050.000 đồng, nếu có tình tiết giảm nhẹ thì mức tiền phạt tối thiểu không thấp hơn 600.000 đồng hoặc có tình tiết tăng nặng thì mức tiền phạt tối đa không quá 1.500.000 đồng đối với hành vi lập hồ sơ khai thuế ghi thiếu, ghi sai các chỉ tiêu làm căn cứ xác định nghĩa vụ thuế trên hóa đơn, chứng từ khác liên quan đến nghĩa vụ thuế.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Phạt tiền 1.400.000 đồng, nếu có tình tiết giảm nhẹ thì mức tiền phạt tối thiểu không thấp hơn 800.000 đồng hoặc có tình tiết tăng nặng thì mức tiền phạt tối đa không quá 2.000.000 đồng đối với hành vi lập hồ sơ khai thuế ghi thiếu, ghi sai các chỉ tiêu làm căn cứ xác định nghĩa vụ thuế trên tờ khai thuế, tờ khai quyết toán thuế.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Phạt tiền 2.100.000 đồng, nếu có tình tiết giảm nhẹ thì mức tiền phạt tối thiểu không thấp hơn 1.200.000 đồng hoặc có tình tiết tăng nặng thì mức tiền phạt tối đa không quá 3.000.000 đồng đối với một trong các hành vi sau đây:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>a) Có hành vi vi phạm:<span xss=removed> khai sai quy định nhưng không dẫn đến thiếu số thuế phải nộp hoặc chưa được hoàn thuế, miễn thuế, giảm thuế hay c</span>ác hành vi được gọi là trốn thuế. </span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>b) Có hành vi khai sai dẫn đến thiếu số thuế phải nộp theo hồ sơ khai thuế tạm tính theo quý nhưng chưa đến thời hạn nộp hồ sơ khai quyết toán thuế năm.</span></span></span></div>', 1488635770, '["5"]', 'http://ketoanbanthoigian.com/images/tram/ketoan-4.jpeg', 'Xử phạt đối với hành vi khai không đầy đủ các nội dung trong hồ sơ thuế', 'Xử phạt đối với hành vi khai không đầy đủ các nội dung trong hồ sơ thuế', 'Xử phạt đối với hành vi khai không đầy đủ các nội dung trong hồ sơ thuếTại Điều 8 của thông thư 166/2013/TT-BTC có ghi các mức xử phạt đối với hành vi khai không đầy đủ các nội dung trong hồ sơ t', 14, 0),
(110, 'Trốn thuế là gì? Các hành vi trốn thuế của doanh nghiệp', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>Trốn thuế là việc thực hiện các phương thức mà pháp luật không cho phép để giảm số thuế phải nộp ví dụ như bán hàng nhưng không xuất hoá đơn để giảm doanh thu hay tạo ra thông tin không có thật như mua hoá đơn để tăng chi phí được khấu trừ thuế, tạo hồ sơ giả để hoàn thuế GTGT ...</strong> </span></span></span></h2>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Theo điều 13 của thông tư 166/2013/TT-BTC - xử phạt vi phạm hành chính về thuế thì doanh nghiệp có các hành vi sau đây sẽ bị coi là trốn thuế:<br>- Không nộp hồ sơ đăng ký thuế; không nộp hồ sơ khai thuế hoặc nộp hồ sơ khai thuế sau 90 ngày, kể từ ngày hết thời hạn nộp hồ sơ khai thuế theo quy định tại các Khoản 1, 2, 3 và 5 Điều 32 Luật quản lý thuế hoặc kể từ ngày hết thời hạn gia hạn nộp hồ sơ khai thuế theo quy định tại Điều 33 của Luật quản lý thuế, trừ trường hợp không phát sinh số thuế phải nộp.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Sử dụng hóa đơn, chứng từ không hợp pháp; sử dụng bất hợp pháp hóa đơn, chứng từ; hóa đơn không có giá trị sử dụng để kê khai thuế làm giảm số thuế phải nộp hoặc tăng số tiền thuế được hoàn, số tiền thuế được miễn, giảm.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Lập thủ tục, hồ sơ hủy vật tư, hàng hóa hoặc giảm số lượng, giá trị vật tư, hàng hóa không đúng thực tế làm giảm số thuế phải nộp hoặc làm tăng số thuế được hoàn, được miễn, giảm.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Lập hóa đơn sai về số lượng, giá trị hàng hoá, dịch vụ bán ra làm căn cứ kê khai nộp thuế thấp hơn thực tế.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Không ghi chép trong sổ kế toán các khoản thu liên quan đến việc xác định số tiền thuế phải nộp; không kê khai, kê khai sai, không trung thực làm giảm số thuế phải nộp hoặc tăng số tiền thuế được hoàn, được miễn, giảm.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Không xuất hóa đơn khi bán hàng hoá, dịch vụ hoặc ghi giá trị trên hóa đơn bán hàng thấp hơn giá trị thanh toán thực tế của hàng hoá, dịch vụ đã bán và bị phát hiện sau thời hạn nộp hồ sơ khai thuế của kỳ tính thuế.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Sử dụng hàng hóa được miễn thuế, xét miễn thuế (bao gồm cả không chịu thuế) không đúng với mục đích quy định mà không khai báo việc chuyển đổi mục đích sử dụng, khai thuế với cơ quan thuế.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Sửa chữa, tẩy xoá chứng từ kế toán, sổ kế toán làm giảm số thuế phải nộp hoặc làm tăng số thuế được hoàn, số tiền thuế được miễn, giảm.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Hủy bỏ chứng từ kế toán, sổ kế toán làm giảm số thuế phải nộp hoặc làm tăng số thuế được hoàn, số tiền thuế được miễn, giảm.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Sử dụng hóa đơn, chứng từ, tài liệu không hợp pháp trong các trường hợp khác để xác định sai số tiền thuế phải nộp, số tiền thuế được hoàn; khai sai căn cứ tính thuế phát sinh số thuế trốn, số thuế gian lận.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Người nộp thuế đang trong thời gian xin tạm ngừng kinh doanh nhưng thực tế vẫn kinh doanh.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Hàng hóa vận chuyển trên đường không có hóa đơn, chứng từ hợp pháp.</span></span></span></div>', 1488635772, '["5"]', 'http://ketoanbanthoigian.com/images/tram/ketoan-3.jpg', 'Trốn thuế là gì? Các hành vi trốn thuế của doanh nghiệp', 'Trốn thuế là gì? Các hành vi trốn thuế của doanh nghiệp', 'Trốn thuế là gì? Các hành vi trốn thuế của doanh nghiệpTrốn thuế là việc thực hiện các phương thức mà pháp luật không cho phép để giảm số thuế phải nộp ví dụ như bán hàng nh', 14, 0),
(111, 'Mức phạt đối với hành vi trốn thuế, gian lận thuế', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Đối với hành vi trốn thuế, gian lận thuế doanh nghi</span>ệp sẽ bị p<span xss=removed>hạt từ 1 đến 3 lần số tiền thuế trốn đối với hành vi trốn thuế, gian lận thuế. </span></span></span></span></h2>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Doanh nghiệp có hành vi trốn thuế, gian lận thuế sẽ bị xử phạt theo số lần tính trên số tiền thuế trốn, số tiền thuế gian lận như sau:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- <strong>Phạt tiền 1 lần</strong> tính trên số thuế trốn, số thuế gian lận đối với người nộp thuế vi phạm lần đầu (trừ hành vi khai sai dẫn đến thiếu thuế ) hoặc vi phạm lần thứ hai mà có từ hai tình tiết giảm nhẹ trở lên.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- <strong>Phạt tiền 1,5</strong> lần tính trên số thuế trốn đối với người nộp thuế khi vi phạm lần đầu, có tình tiết tăng nặng hoặc vi phạm lần thứ hai, có một tình tiết giảm nhẹ.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- <strong>Phạt tiền 2 lần</strong> tính trên số thuế trốn đối với người nộp thuế khi </span></span></span><span xss=removed>vi phạm</span><span xss=removed> lần thứ hai mà không có tình tiết giảm nhẹ hoặc vi phạm lần thứ ba và có một tình tiết giảm nhẹ.</span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- <strong>Phạt tiền 2,5 lần</strong> tính trên số thuế trốn đối với người nộp thuế khi vi phạm lần thứ hai mà có một tình tiết tăng nặng hoặc vi phạm lần thứ ba mà không có tình tiết giảm nhẹ.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>- <strong>Phạt tiền 3 lần</strong> tính trên số tiền thuế trốn đối với người nộp thuế khi vi phạm lần thứ hai mà có từ hai tình tiết tăng nặng trở lên hoặc vi phạm lần thứ ba có tình tiết tăng nặng hoặc vi phạm từ lần thứ tư trở đi.</span></span></span></span>\r\n<p><span xss=removed><strong>Chú ý:</strong></span></p>\r\n</div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Các hành vi trốn thuế, gian lận thuế bị xử phạt và còn bị buộc áp dụng biện pháp khắc phục hậu quả là nộp đủ số tiền thuế trốn, gian lận vào ngân sách nhà nước.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Các hành vi vi phạm quy định trên bị phát hiện trong thời hạn nộp hồ sơ khai thuế của kỳ tính thuế hoặc bị phát hiện sau thời hạn nộp hồ sơ khai thuế, nhưng không làm giảm số thuế phải nộp hoặc chưa được hoàn, không làm tăng số thuế miễn, giảm và không thuộc trường hợp bị xử phạt đối với hành vi trốn thuế thì bị xử phạt về hành vi vi phạm về thủ tục thuế (1,2tr - 3tr)</span></span></span></div>\r\n<div> </div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Trường hợp, người nộp thuế nộp hồ sơ khai thuế sau 90 ngày, kể từ ngày hết thời hạn nộp hồ sơ khai thuế, đã tự giác nộp đầy đủ số tiền thuế phải nộp vào ngân sách nhà nước trước thời điểm cơ quan thuế lập biên bản vi phạm hành chính về hành vi chậm nộp hồ sơ khai thuế thì bị xử phạt về hành vi chậm nộp hồ sơ khai thuế .</span></span></span></div>\r\n<div xss=removed><span xss=removed>Trường hợp, người nộp thuế đã nộp hồ sơ khai thuế nhưng khai sai, gian lận thuế dẫn đến thiếu số tiền thuế phải nộp, gian lận, trốn thuế thì ngoài việc bị xử phạt về thủ tục thuế còn bị xử phạt về khai thiếu thuế hoặc trốn thuế.</span></div>\r\n<div xss=removed> </div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Mức phạt t</span>ội trốn thuế này được <span xss=removed>quy định tại Điều 13 Thông tư 166/2013/TT-BTC </span>có hiệu lực từ ngày 1/1/2014<span xss=removed> là mức phạt tiền áp dụng đối với người nộp thuế là tổ chức, mức phạt tiền đối với người nộp thuế là hộ gia đình, cá nhân bằng ½ mức phạt tiền đối với tổ chức.</span></span></span></span></div>', 1488635773, '["5"]', 'http://ketoanbanthoigian.com/images/tram/ketoan-2.jpg', 'Mức phạt đối với hành vi trốn thuế, gian lận thuế', 'Mức phạt đối với hành vi trốn thuế, gian lận thuế', 'Mức phạt đối với hành vi trốn thuế, gian lận thuếĐối với hành vi trốn thuế, gian lận thuế doanh nghiệp sẽ bị phạt từ 1 đến 3 lần số tiền thuế trốn đối với hành vi tr', 14, 0);
INSERT INTO `blogs` (`blog_id`, `blog_name`, `blog_content`, `blog_time`, `blog_cat_ids`, `blog_image`, `blog_seo_title`, `blog_seo_keyword`, `blog_seo_description`, `blog_user_id`, `blog_views`) VALUES
(112, 'Mức phạt chậm nộp tiền thuế GTGT, TNDN và cách tính tiền phạt 2016', '<div class="name1" xss=removed>Mức phạt chậm nộp tiền thuế GTGT, TNDN và cách tính tiền phạt 2016</div>\r\n<div class="h15" xss=removed> </div>\r\n<div class="contentNew" xss=removed>\r\n<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Bắt đầu từ ngày 1/7/2016 việc xử phạt vi phạm nộp chậm tiền thuế sẽ được thực hiện theo quy định tại Tại Khoản 3 Điều 3 Luật số 106/2016/QH13 quy định: </span></span></span><br><span xss=removed><span xss=removed><span xss=removed>“Người nộp thuế chậm nộp tiền thuế so với thời hạn quy định, thời hạn gia hạn nộp thuế, thời hạn ghi trong thông báo của cơ quan quản lý thuế, thời hạn trong quyết định xử lý của cơ quan quản lý thuế thì phải nộp đủ tiền thuế và </span></span></span><span xss=removed><span xss=removed><span xss=removed>tiền chậm nộp theo mức bằng 0,03%/ngày tính trên số tiền thuế chậm nộp.</span></span></span></h2>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><em>Đối với các khoản nợ tiền thuế phát sinh trước ngày 01 tháng 7 năm 2016 mà người nộp thuế chưa nộp vào ngân sách nhà nước, kể cả khoản tiền nợ thuế được truy thu qua kết quả thanh tra, kiểm tra của cơ quan có thẩm quyền thì được chuyển sang áp dụng mức tính tiền chậm nộp theo quy định tại Khoản này từ ngày 01 thảng 7 năm 2016. ”</em></span></span></span></span>\r\n<p>Căn cứ hướng dẫn trên, đối với khoản nợ tiền thuế phát sinh trước ngày 01/7/2016 nhưng sau ngày 01/7/2016 vẫn chưa nộp thì từ ngày 01/7/2016 chuyển sang áp dụng mức tính tiền chậm nộp 0,03%/ngày.</p>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>Ví dụ</strong></span>: Công ty TNHH A có số thuế GTGT phát sinh của quý I/2016 là 150 triệu đồng, thời hạn cuối cùng phải nộp là 30/4/2016, nhưng đến ngày 30/7/2016 Công ty mới thực hiện nộp khoản thuế này. Như vây, Công ty sẽ phải nộp tiền chậm nộp và phải tính theo tính theo hai mức: Từ ngày 01/5/2016 đến hết ngày 30/6/2016 áp dụng mức tính chậm nộp là 0,05%/ngày. Từ ngày 01/7/2016 đến ngày 29/7/2016 áp dụng mức tính tiền chậm nộp 0,03%/ngày. </span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Trường hợp người nộp thuế khai thiếu tiền thuế của kỳ thuế phát sinh trước ngày 01/7/2016 nhưng sau ngày 01/7/2016, cơ quan nhà nước có thẩm quyền phát hiện qua thanh tra, kiểm tra hoặc người nộp thuế tự phát hiện thì áp dụng tiền chậm nộp theo mức 0,05%/ngày (hoặc mức phù hợp quy định của văn bản pháp luật từng thời kỳ) từ ngày phải nộp theo quy định của pháp luật đến hết ngày 30/6/2016 và theo mức 0,03%/ngày tính trên số tiền thuế khai thiếu từ ngày 01/7/2016 đến ngày người nộp thuế nộp vào ngân sách nhà nước.</span></span></span></span></span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>Ví dụ:</strong></span> Công ty cổ phần X đã khai tờ khai quyết toán thuế thu nhập doanh nghiệp năm 2015 và đến hết ngày 31/3/2016 đã nộp hết số thuế phát sinh phải nộp trên tờ khai. Tuy nhiên, đến ngày 25/7/2016 Công ty phát hiện khai sai và phải làm tờ khai bổ sung, số thuế TNDN phải nộp thêm là 250 triệu đồng. Số tiền thuế phải nộp bổ sung (250 triệu đồng) cũng sẽ áp dụng mức tính tiền chậm nộp theo hai mức: Từ ngày 01/4/2016 đến ngày 30/6/2016 áp dụng mức tính chậm nộp là 0,05%/ngày. Từ ngày 01/7/2016 đến ngày 25/7/2016 áp dụng mức tính tiền chậm nộp 0,03%/ngày. </span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><br><span xss=removed><span xss=removed><span xss=removed>(<em>Theo hướng dẫn tại công văn Số: 10315/BTC-TCT V/v thực hiện Luật số 106/2016/QH13Hà Nội, ngày 25 tháng 7 năm 2016</em>)</span></span></span></span></span></span></span>\r\n<div> </div>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><br><span xss=removed><strong><span xss=removed><span xss=removed>Trước ngày 1/7/2016. Thì</span></span>  <span xss=removed><span xss=removed>cách xác định tiền chậm nộp tiền thuế được tính </span></span><span xss=removed><span xss=removed>theo thông tư 26/2015/TT-BTC</span></span> </strong><span xss=removed><span xss=removed><strong>như sau:</strong></span></span></span><br><span xss=removed><span xss=removed><span xss=removed>1.  Đối với khoản tiền thuế nợ phát sinh từ ngày 01/01/2015 thì tiền chậm nộp được tính theo mức <strong>0,05%</strong>số tiền thuế chậm nộp tính trên mỗi ngày chậm nộp.</span></span></span><br></span></span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>(</span></span></span><span xss=removed>Bỏ quy định mức phạt chậm nộp 0.07% đối với việc chậm nộp tiền từ ngày 91 trở đi.)</span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><br><span xss=removed><span xss=removed><span xss=removed>2. Đối với khoản tiền thuế nợ phát sinh trước ngày 01/01/2015 nhưng sau ngày 01/01/2015 vẫn chưa nộp thì tính như sau: trước ngày 01/01/2015 tính phạt chậm nộp, tiền chậm nộp theo quy định tại Luật quản lý thuế số 78/2006/QH11, Luật sửa đổi, bổ sung một số điều của Luật quản lý thuế số 21/2012/QH13, từ ngày 01/01/2015 tính tiền chậm nộp theo quy định tại Luật sửa đổi, bổ sung một số điều của các luật về thuế số 71/2014/QH13.</span></span></span></span></span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Ví dụ: Người nộp thuế B nợ 100 triệu đồng tiền thuế GTGT thuộc tờ khai thuế GTGT tháng 8/2014, thời hạn nộp thuế chậm nhất là ngày 22/9/2014 (do ngày 20/9/2014 và ngày 21/9/2014 là ngày nghỉ). Ngày 20/01/2015 người nộp thuế nộp số tiền thuế này vào ngân sách nhà nước, số ngày chậm nộp được tính từ ngày 23/9/2014 đến ngày 20/01/2015, số tiền chậm nộp phải nộp là 6,2 triệu đồng. Cụ thể như sau:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Trước ngày 01/01/2015 tiền chậm nộp được tính như sau:<br>  Từ ngày 23/9/2014 đến ngày 21/12/2014, số ngày chậm nộp là 90 ngày:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>100 triệu x 0,05% x 90 ngày = 4,5 triệu đồng.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Từ ngày 22/12/2014 đến ngày 31/12/2014, số ngày chậm nộp là 10 ngày:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>100 triệu x 0,07% x 10 ngày = 0,7 triệu đồng.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Từ ngày 01/01/2015 đến ngày 20/01/2015, số ngày chậm nộp là 20 ngày:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>100 triệu x 0,05% x 20 ngày = 1 triệu đồng.”<br>(<span xss=removed><strong>Cách tính này chỉ áp dụng cho trường hợp NỢ THUẾ - Tức là năm 2014 bạn đã kê khai thuế ra số tiền phải nộp nhưng bạn chưa đi nộp</strong></span>)</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><br><span xss=removed><span xss=removed><span xss=removed>3. Trường hợp người nộp thuế khai thiếu tiền thuế của kỳ thuế phát sinh <strong>trước</strong> ngày 01/01/2015 nhưng sau ngày 01/01/2015, cơ quan nhà nước có thẩm quyền phát hiện qua thanh tra, kiểm tra hoặc người nộp thuế tự phát hiện thì áp dụng tiền chậm nộp theo mức 0,05%/ngày tính trên số tiền thuế khai thiếu từ ngày phải nộp theo quy định của pháp luật đến ngày người nộp thuế nộp tiền thuế khai thiếu vào ngân sách nhà nước.”</span></span></span></span></span></span></span>\r\n<p><span xss=removed><span xss=removed><span xss=removed><strong>Chú ý: Không tính tiền chậm nộp:</strong></span></span></span><br><span xss=removed><span xss=removed><span xss=removed>1. Người nộp thuế cung ứng hàng hoá, dịch vụ được thanh toán bằng nguồn vốn ngân sách nhà nước nhưng chưa được đơn vị sử dụng vốn ngân sách nhà nước thanh toán (sau đây gọi chung là người nộp thuế) nên không nộp thuế kịp thời dẫn đến nợ thuế thì không phải nộp tiền chậm nộp tiền thuế.<br>Trường hợp người nộp thuế cung ứng hàng hoá, dịch vụ được thanh toán một phần từ nguồn vốn ngân sách nhà nước, phần còn lại từ nguồn ngoài ngân sách, nhưng chưa được đơn vị sử dụng vốn ngân sách nhà nước thanh toán nên không nộp thuế kịp thời dẫn đến nợ thuế thì người nộp thuế không phải nộp tiền chậm nộp đối với số tiền thuế tương ứng với phần được thanh toán từ ngân sách nhà nước.<br>Đơn vị sử dụng vốn ngân sách nhà nước là đơn vị mở tài khoản tại Kho bạc nhà nước được giao dự toán chi ngân sách nhà nước theo quy định của Luật ngân sách nhà nước.<br>Ví dụ: Người nộp thuế A cung cấp hàng hóa X cho đơn vị B (đơn vị sử dụng vốn ngân sách nhà nước) giá trị hàng hóa X là 100 triệu đồng, trong đó 40 triệu được thanh toán từ NSNN, 60 triệu được thanh toán từ nguồn ngoài ngân sách nhà nước. Người nộp thuế A chưa được đơn vị B thanh toán 100 triệu đồng.<br>Người nộp thuế A nợ thuế là 70 triệu đồng, người nộp thuế A thuộc trường hợp không phải nộp tiền chậm nộp đối với số thuế là 40 triệu đồng.<br>2. Phạm vi số tiền thuế và thời gian không tính tiền chậm nộp tiền thuế đối với trường hợp người nộp thuế cung ứng hàng hoá, dịch vụ được thanh toán bằng nguồn vốn ngân sách nhà nước nhưng chưa được đơn vị sử dụng vốn ngân sách nhà nước thanh toán<br>a) Không tính tiền chậm nộp đối với số tiền thuế còn nợ, số tiền thuế nợ này không vượt quá số tiền ngân sách nhà nước chưa thanh toán cho người nộp thuế.<br>Trường hợp người nộp thuế nợ thuế của nhiều kỳ kê khai thuế thì tổng số thuế nợ của các kỳ kê khai không vượt quá số tiền ngân sách nhà nước chưa thanh toán.<br>b) Thời gian không tính chậm nộp tiền thuế được tính từ ngày người nộp thuế phải nộp thuế đến ngày đơn vị sử dụng vốn ngân sách nhà nước thanh toán cho người nộp thuế và không vượt quá thời gian ngân sách nhà nước chưa thanh toán cho người nộp thuế.<br>3. Trình tự, thủ tục không tính tiền chậm nộp<br>a) Người nộp thuế thuộc trường hợp không phải nộp tiền chậm nộp theo quy định tại khoản 1 Điều này cung cấp cho cơ quan thuế quản lý trực tiếp văn bản xác nhận của đơn vị sử dụng vốn ngân sách nhà nước về việc người nộp thuế chưa được thanh toán theo mẫu số 01/TCN ban hành kèm theo Thông tư này.<br>b) Cơ quan thuế ban hành quyết định về việc kiểm tra tại trụ sở của người nộp thuế, thời gian kiểm tra tối đa là 03 ngày làm việc. Sau khi kiểm tra, cơ quan thuế xác định:<br>- Nếu người nộp thuế thuộc trường hợp không phải nộp tiền chậm nộp thì cơ quan thuế ban hành thông báo về việc người nộp thuế không phải nộp tiền chậm nộp thuế.<br>- Nếu người nộp thuế thuộc trường hợp phải nộp tiền chậm nộp thì cơ quan thuế ban hành thông báo về việc người nộp thuế phải nộp tiền chậm nộp thuế (thông báo tiền thuế nợ, tiền phạt và tiền chậm nộp) và cưỡng chế thi hành quyết định hành chính thuế theo quy định của pháp luật.<br>Ví dụ:<br>Ngày 20/02/2015, người nộp thuế A nộp tờ khai thuế GTGT, số thuế phải nộp là 30 triệu đồng. Tại thời điểm này, ngân sách nhà nước chưa thanh toán cho người nộp thuế A số tiền là 100 triệu đồng. Sau khi kiểm tra, cơ quan thuế xác định người nộp thuế A thuộc trường hợp không phải nộp tiền chậm nộp đối với số thuế GTGT 30 triệu đồng đến khi được ngân sách nhà nước thanh toán.<br>Đến ngày 31/3/2015, người nộp thuế A nộp quyết toán thuế TNDN, số tiền thuế TNDN phải nộp là 80 triệu đồng. Tại thời điểm này, ngân sách nhà nước vẫn chưa thanh toán cho người nộp thuế A số tiền 100 triệu đồng. Người nộp thuế A tiếp tục thuộc trường hợp không phải nộp tiền chậm nộp đối với số thuế TNDN 70 triệu đồng đến khi được ngân sách nhà nước thanh toán. Số tiền thuế còn lại là 10 triệu đồng, nếu người nộp thuế A chưa nộp vào ngân sách nhà nước theo quy định thì phải tính tiền chậm nộp.<br>c) Sau khi được ngân sách nhà nước thanh toán, người nộp thuế thực hiện nộp tiền thuế vào ngân sách nhà nước và thông báo cho cơ quan thuế theo mẫu số 02/TCN ban hành kèm theo Thông tư này để cơ quan thuế có cơ sở tính lại các khoản tiền thuế nợ, tiền chậm nộp và xác định chính xác số ngày không phải nộp tiền chậm nộp của người nộp thuế.</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed><strong>Xác định số tiền chậm nộp được miễn</strong></span></span></span><br><span xss=removed><span xss=removed><span xss=removed>a) Trường hợp người nộp thuế gặp thiên tai, hỏa hoạn, tai nạn bất ngờ, dịch bệnh: số tiền chậm nộp được miễn tính trên số tiền thuế còn nợ tại thời điểm xảy ra thiên tai, hỏa hoạn, tai nạn bất ngờ, dịch bệnh và số tiền chậm nộp được miễn này không vượt quá giá trị tài sản, hàng hoá bị thiệt hại.<br>b) Trường hợp người nộp thuế bị bệnh hiểm nghèo: số tiền chậm nộp được miễn tính trên số tiền thuế còn nợ tại thời điểm mắc bệnh hiểm nghèo và số tiền chậm nộp được miễn này không vượt quá chi phí khám, chữa bệnh.”<br>c) Trường hợp NNT gặp bất khả kháng khác: số tiền chậm nộp được miễn tính trên số tiền thuế còn nợ tại thời điểm gây nên tình trạng bất khả kháng khác và số tiền chậm nộp được miễn này không vượt quá giá trị tài sản, hàng hoá bị thiệt hại.”<br>b) Sửa đổi, bổ sung điểm b.1, b.2 khoản 3 như sau:<br>“b.1) Trường hợp do thiên tai, hoả hoạn, tai nạn bất ngờ, dịch bệnh, phải có:<br>- Biên bản xác định mức độ, giá trị thiệt hại về tài sản của cơ quan có thẩm quyền như Hội đồng định giá do Sở Tài chính thành lập, hoặc các công ty định giá chuyên nghiệp cung cấp dịch vụ định giá theo hợp đồng, hoặc Trung tâm định giá của Sở Tài chính;<br>- Văn bản xác nhận về việc người nộp thuế có thiệt hại tại nơi xảy ra thiên tai, hỏa hoạn, tai nạn bất ngờ và thời điểm xảy ra thiên tai, hỏa hoạn, tai nạn bất ngờ, dịch bệnh của một trong các cơ quan, tổ chức sau: công an cấp xã, phường hoặc UBND cấp xã, phường, Ban quản lý Khu công nghiệp, Khu chế xuất, Khu kinh tế nơi xảy ra thiên tai, hoả hoạn, tai nạn bất ngờ hoặc tổ chức cứu hộ, cứu nạn;<br>- Hồ sơ bồi thường thiệt hại được cơ quan bảo hiểm chấp nhận bồi thường (nếu có);<br>- Hồ sơ quy định trách nhiệm của tổ chức, cá nhân phải bồi thường (nếu có).<br>b.2) Trường hợp cá nhân bị bệnh hiểm nghèo thì phải có xác nhận đã khám chữa bệnh trên sổ y bạ, thời điểm xác nhận của cơ sở khám chữa bệnh được thành lập theo quy định của pháp luật; chi phí khám, chữa bệnh có đầy đủ chứng từ theo quy định; hồ sơ thanh toán chi phí khám, chữa bệnh của cơ quan bảo hiểm (nếu có).”<br>c) Bổ sung Khoản 5 vào Điều 35 như sau:<br>“5. Trình tự giải quyết hồ sơ miễn tiền chậm nộp<br>a) Trong thời hạn 60 (sáu mươi) ngày kể từ ngày xảy ra gặp thiên tai, hỏa hoạn, tai nạn bất ngờ, dịch bệnh, bệnh hiểm nghèo hoặc trường hợp bất khả kháng khác người nộp thuế phải lập hồ sơ đề nghị miễn tiền chậm nộp gửi đến cơ quan thuế quản lý trực tiếp.<br>b) Trường hợp hồ sơ đề nghị miễn tiền chậm nộp chưa đầy đủ theo quy định, trong thời hạn 03 (ba) ngày làm việc, kể từ ngày tiếp nhận hồ sơ, cơ quan thuế phải thông báo bằng văn bản cho người nộp thuế đề nghị giải trình hoặc bổ sung hồ sơ. Người nộp thuế phải giải trình hoặc bổ sung hồ sơ trong thời hạn 10 (mười) ngày làm việc kể từ ngày cơ quan thuế ban hành văn bản giải trình hoặc bổ sung hồ sơ.<br>Nếu người nộp thuế không hoàn chỉnh hồ sơ theo yêu cầu của cơ quan thuế thì người nộp thuế không thuộc trường hợp được miễn tiền chậm nộp.<br>c) Trường hợp hồ sơ đề nghị miễn tiền chậm nộp đầy đủ, trong thời hạn 10 (mười) ngày làm việc kể từ ngày nhận được hồ sơ, cơ quan thuế quản lý trực tiếp ban hành văn bản gửi người nộp thuế:<br>c.1) Văn bản không chấp thuận đề nghị của người nộp thuế nếu người nộp thuế không đủ điều kiện được miễn tiền chậm nộp.<br>c.2) Quyết định về việc miễn tiền chậm nộp nếu người nộp thuế thuộc trường hợp được miễn tiền chậm nộp.”</span></span></span></p>\r\n<h3 xss=removed><span xss=removed><span xss=removed><span xss=removed>Trước đây việc tính số tiền thuế nộp chậm được thực hiện theo điều 34 của TT 156/2013/TT-BTC có hướng dẫn về cách tính tiền phạt đối với việc chậm nộp tiền thuế như sau:</span></span></span></h3>\r\n<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>  </span></span></span><strong><span xss=removed><span xss=removed><span xss=removed>Trong vòng 90 ngày đầu tiên thì nhân với 0.05%.</span></span></span></strong></h2>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Công thức: Số tiền phạt chậm nộp = số tiền thuế nộp chậm X 0.05% X số ngày nộp chậm</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>  </span></span></span><span xss=removed><span xss=removed><span xss=removed><strong>Từ ngày 91 trở đi nhân với 0.07%</strong></span></span></span></span></span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Công thức: Số tiền phạt chậm nộp = số tiền thuế nộp chậm X 0.07% X số ngày nộp chậm</span></span></span>\r\n<div xss=removed> </div>\r\n</div>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><br><span xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>Ví dụ:</strong></span> Kỳ tính thuế GTGT tháng 7 năm 2013 Công ty ABC có phát sinh số tiền phải nộp là 50.000.000 Nhưng đến ngày 11/12/2013 Công ty <span xss=removed>ABC </span> mới nộp tiền thì số tiền thuế nộp chậm được xác định như sau:<br>- Hạn nộp tiền thuế GTGT của tháng 7/2013 là ngày 20/8/2013 vậy là thời gian nộp chậm được tính bắt đầu từ ngày 21/8/2013 đến ngày 11/12/2013.<br>- Tổng thời gian nộp chậm là: 113 ngày<br>=', 1488635775, '["5"]', 'http://ketoanbanthoigian.com/images/tram/ketoan-1.jpg', 'Mức phạt chậm nộp tiền thuế GTGT, TNDN và cách tính tiền phạt 2016', 'Mức phạt chậm nộp tiền thuế GTGT, TNDN và cách tính tiền phạt 2016', 'Mức phạt chậm nộp tiền thuế GTGT, TNDN và cách tính tiền phạt 2016Mức phạt chậm nộp tiền thuế GTGT, TNDN và cách tính tiền phạt 2016\r\n \r\n\r\nBắt đầu từ ngày 1/7/2016 việc xử phạt vi phạm nộp', 14, 0),
(113, 'Cách xác định nguyên giá của TSCĐ hữu hình, vô hình, thuê tài chính', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Quy định mới nhất hiện nay về tài sản cố định là thông tư 45/2013/TT-BTC có hiệu lực từ ngày 10/6/2013. Để được gọi là tài sản cố định thì tản sản đó phải có giá trị từ 30 triệu đồng trở lên. </span></span></span><span xss=removed>Tại điều 4 của thông tư có hướng dẫn về cách xác định nguyên giá của TSCĐ như sau:</span></h2>\r\n<h4 xss=removed><span xss=removed><span xss=removed><span xss=removed>1. Cách xác định nguyên giá tài sản cố định hữu hình:</span></span></span></h4>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>a) TSCĐ hữu hình mua sắm:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ hữu hình mua sắm (kể cả mua mới và cũ): là giá mua thực tế phải trả cộng ( ) các khoản thuế (không bao gồm các khoản thuế được hoàn lại), các chi phí liên quan trực tiếp phải chi ra tính đến thời điểm đưa tài sản cố định vào trạng thái sẵn sàng sử dụng như: lãi tiền vay phát sinh trong quá trình đầu tư mua sắm tài sản cố định; chi phí vận chuyển, bốc dỡ; chi phí nâng cấp; chi phí lắp đặt, chạy thử; lệ phí trước bạ và các chi phí liên quan trực tiếp khác.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Trường hợp TSCĐ hữu hình mua trả chậm, trả góp, nguyên giá TSCĐ là giá mua trả tiền ngay tại thời điểm mua cộng ( ) các khoản thuế (không bao gồm các khoản thuế được hoàn lại), các chi phí liên quan trực tiếp phải chi ra tính đến thời điểm đưa TSCĐ vào trạng thái sẵn sàng sử dụng như: chi phí vận chuyển, bốc dỡ; chi phí nâng cấp; chi phí lắp đặt, chạy thử; lệ phí trước bạ (nếu có).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Trường hợp mua TSCĐ hữu hình là nhà cửa, vật kiến trúc gắn liền với quyền sử dụng đất thì giá trị quyền sử dụng đất phải xác định riêng và ghi nhận là TSCĐ vô hình nếu đáp ứng đủ tiêu chuẩn theo quy định tại điểm đ khoản 2 Điều này, còn TSCĐ hữu hình là nhà cửa, vật kiến trúc thì nguyên giá là giá mua thực tế phải trả cộng ( ) các khoản chi phí liên quan trực tiếp đến việc đưa TSCĐ hữu hình vào sử dụng.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Trường hợp sau khi mua TSCĐ hữu hình là nhà cửa, vật kiến trúc gắn liền với quyền sử dụng đất, doanh nghiệp dỡ bỏ hoặc huỷ bỏ để xây dựng mới thì giá trị quyền sử dụng đất phải xác định riêng và ghi nhận là TSCĐ vô hình nếu đáp ứng đủ tiêu chuẩn theo quy định tại điểm đ khoản 2 Điều này; nguyên giá của TSCĐ xây dựng mới được xác định là giá quyết toán công trình đầu tư xây dựng theo quy định tại Quy chế quản lý đầu tư và xây dựng hiện hành. Những tài sản dỡ bỏ hoặc huỷ bỏ được xử lý hạch toán theo quy định hiện hành đối với thanh lý tài sản cố định.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>b) TSCĐ hữu hình mua theo hình thức trao đổi:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ mua theo hình thức trao đổi với một TSCĐ hữu hình không tương tự hoặc tài sản khác là giá trị hợp lý của TSCĐ hữu hình nhận về, hoặc giá trị hợp lý của TSCĐ đem trao đổi (sau khi cộng thêm các khoản phải trả thêm hoặc trừ đi các khoản phải thu về) cộng ( ) các khoản thuế (không bao gồm các khoản thuế được hoàn lại), các chi phí liên quan trực tiếp phải chi ra đến thời điểm đưa TSCĐ vào trạng thái sẵn sàng sử dụng, như: chi phí vận chuyển, bốc dỡ; chi phí nâng cấp; chi phí lắp đặt, chạy thử; lệ phí trước bạ (nếu có).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ hữu hình mua theo hình thức trao đổi với một TSCĐ hữu hình tương tự, hoặc có thể hình thành do được bán để đổi lấy quyền sở hữu một tài sản tương tự là giá trị còn lại của TSCĐ hữu hình đem trao đổi.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>c) Tài sản cố định hữu hình tự xây dựng hoặc tự sản xuất:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ hữu hình tự xây dựng là giá trị quyết toán công trình khi đưa vào sử dụng. Trường hợp TSCĐ đã đưa vào sử dụng nhưng chưa thực hiện quyết toán thì doanh nghiệp hạch toán nguyên giá theo giá tạm tính và điều chỉnh sau khi quyết toán công trình hoàn thành.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ hữu hình tự sản xuất là giá thành thực tế của TSCĐ hữu hình cộng ( ) các chi phí lắp đặt chạy thử, các chi phí khác trực tiếp liên quan tính đến thời điểm đưa TSCĐ hữu hình vào trạng thái sẵn sàng sử dụng (trừ các khoản lãi nội bộ, giá trị sản phẩm thu hồi được trong quá trình chạy thử, sản xuất thử, các chi phí không hợp lý như vật liệu lãng phí, lao động hoặc các khoản chi phí khác vượt quá định mức quy định trong xây dựng hoặc sản xuất).</span></span></span></div>\r\n<div><img src="/images/hinh-anh/ad.jpg" alt="ad" xss=removed></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>d) Nguyên giá tài sản cố định hữu hình do đầu tư xây dựng:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ do đầu tư xây dựng cơ bản hình thành theo phương thức giao thầu là giá quyết toán công trình xây dựng theo quy định tại Quy chế quản lý đầu tư và xây dựng hiện hành cộng ( ) lệ phí trước bạ, các chi phí liên quan trực tiếp khác. Trường hợp TSCĐ do đầu tư xây dựng đã đưa vào sử dụng nhưng chưa thực hiện quyết toán thì doanh nghiệp hạch toán nguyên giá theo giá tạm tính và điều chỉnh sau khi quyết toán công trình hoàn thành.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Đối với tài sản cố định là con súc vật làm việc và/ hoặc cho sản phẩm, vườn cây lâu năm thì nguyên giá là toàn bộ các chi phí thực tế đã chi ra cho con súc vật, vườn cây đó từ lúc hình thành tính đến thời điểm đưa vào khai thác, sử dụng.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>đ) Tài sản cố định hữu hình được tài trợ, được biếu, được tặng, do phát hiện thừa:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá tài sản cố định hữu hình được tài trợ, được biếu, được tặng, do phát hiện thừa là giá trị theo đánh giá thực tế của Hội đồng giao nhận hoặc tổ chức định giá chuyên nghiệp.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>e) Tài sản cố định hữu hình được cấp; được điều chuyển đến:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ hữu hình được cấp, được điều chuyển đến bao gồm giá trị còn lại của TSCĐ trên số kế toán ở đơn vị cấp, đơn vị điều chuyển hoặc giá trị theo đánh giá thực tế của tổ chức định giá chuyên nghiệp theo quy định của pháp luật, cộng ( ) các chi phí liên quan trực tiếp mà bên nhận tài sản phải chi ra tính đến thời điểm đưa TSCĐ vào trạng thái sẵn sàng sử dụng như chi phí thuê tổ chức định giá; chi phí nâng cấp, lắp đặt, chạy thử…</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>g) Tài sản cố định hữu hình nhận góp vốn, nhận lại vốn góp:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>TSCĐ nhận góp vốn, nhận lại vốn góp là giá trị do các thành viên, cổ đông sáng lập định giá nhất trí; hoặc doanh nghiệp và người góp vốn thoả thuận; hoặc do tổ chức chuyên nghiệp định giá theo quy định của pháp luật và được các thành viên, cổ đông sáng lập chấp thuận.</span></span></span></div>\r\n<div xss=removed> <img src="/images/tin-tuc/accounting.jpg" alt="accounting"></div>\r\n<h4 xss=removed><span xss=removed><span xss=removed><span xss=removed>2. Xác định nguyên giá tài sản cố định vô hình:</span></span></span></h4>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>a) Tài sản cố định vô hình mua sắm:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ vô hình mua sắm là giá mua thực tế phải trả cộng ( ) các khoản thuế (không bao gồm các khoản thuế được hoàn lại) và các chi phí liên quan trực tiếp phải chi ra tính đến thời điểm đưa tài sản vào sử dụng.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Trường hợp TSCĐ vô hình mua sắm theo hình thức trả chậm, trả góp, nguyên giá TSCĐ là giá mua tài sản theo phương thức trả tiền ngay tại thời điểm mua (không bao gồm lãi trả chậm).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>b) Tài sản cố định vô hình mua theo hình thức trao đổi:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ vô hình mua theo hình thức trao đổi với một TSCĐ vô hình không tương tự hoặc tài sản khác là giá trị hợp lý của TSCĐ vô hình nhận về, hoặc giá trị hợp lý của tài sản đem trao đổi (sau khi cộng thêm các khoản phải trả thêm hoặc trừ đi các khoản phải thu về) cộng ( ) các khoản thuế (không bao gồm các khoản thuế được hoàn lại), các chi phí liên quan trực tiếp phải chi ra tính đến thời điểm đưa tài sản vào sử dụng theo dự tính.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ vô hình mua dưới hình thức trao đổi với một TSCĐ vô hình tương tự, hoặc có thể hình thành do được bán để đổi lấy quyền sở hữu một tài sản tương tự là giá trị còn lại của TSCĐ vô hình đem trao đổi.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>c) Tài sản cố định vô hình được cấp, được biếu, được tặng, được điều chuyển đến:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ vô hình được cấp, được biếu, được tặng là giá trị hợp lý ban đầu cộng ( ) các chi phí liên quan trực tiếp phải chi ra tính đến việc đưa tài sản vào sử dụng.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ được điều chuyển đến là nguyên giá ghi trên sổ sách kế toán của doanh nghiệp có tài sản điều chuyển. Doanh nghiệp tiếp nhận tài sản điều chuyển có trách nhiệm hạch toán nguyên giá, giá trị hao mòn, giá trị còn lại của tài sản theo quy định.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>d) Tài sản cố định vô hình được tạo ra từ nội bộ doanh nghiệp:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ vô hình được tạo ra từ nội bộ doanh nghiệp là các chi phí liên quan trực tiếp đến khâu xây dựng, sản xuất thử nghiệm phải chi ra tính đến thời điểm đưa TSCĐ đó vào sử dụng theo dự tính.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Riêng các chi phí phát sinh trong nội bộ để doanh nghiệp có nhãn hiệu hàng hoá, quyền phát hành, danh sách khách hàng, chi phí phát sinh trong giai đoạn nghiên cứu và các khoản mục tương tự không đáp ứng được tiêu chuẩn và nhận biết TSCĐ vô hình được hạch toán vào chi phí kinh doanh trong kỳ.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>đ) TSCĐ vô hình là quyền sử dụng đất:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- TSCĐ vô hình là quyền sử dụng đất bao gồm:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Quyền sử dụng đất được nhà nước giao có thu tiền sử dụng đất hoặc nhận chuyển nhượng quyền sử dụng đất hợp pháp (bao gồm quyền sử dụng đất có thời hạn, quyền sử dụng đất không thời hạn).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Quyền sử dụng đất thuê trước ngày có hiệu lực của Luật Đất đai năm 2003 mà đã trả tiền thuê đất cho cả thời gian thuê hoặc đã trả trước tiền thuê đất cho nhiều năm mà thời hạn thuê đất đã được trả tiền còn lại ít nhất là năm năm và được cơ quan có thẩm quyền cấp giấy chứng nhận quyền sử dụng đất.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ là quyền sử dụng đất được xác định là toàn bộ khoản tiền chi ra để có quyền sử dụng đất hợp pháp cộng ( ) các chi phí cho đền bù giải phóng mặt bằng, san lấp mặt bằng, lệ phí trước bạ (không bao gồm các chi phí chi ra để xây dựng các công trình trên đất); hoặc là giá trị quyền sử dụng đất nhận góp vốn.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Quyền sử dụng đất không ghi nhận là TSCĐ vô hình gồm:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Quyền sử dụng đất được Nhà nước giao không thu tiền sử dụng đất.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Thuê đất trả tiền thuê một lần cho cả thời gian thuê (thời gian thuê đất sau ngày có hiệu lực thi hành của Luật đất đai năm 2003, không được cấp giấy chứng nhận quyền sử dụng đất) thì tiền thuê đất được phân bổ dần vào chi phí kinh doanh theo số năm thuê đất.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Thuê đất trả tiền thuê hàng năm thì tiền thuê đất được hạch toán vào chi phí kinh doanh trong kỳ tương ứng số tiền thuê đất trả hàng năm.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Đối với các loại tài sản là nhà, đất đai để bán, để kinh doanh của công ty kinh doanh bất động sản thì doanh nghiệp không được hạch toán là TSCĐ và không được trích khấu hao.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>e) Nguyên giá của TSCĐ vô hình là quyền tác giả, quyền sở hữu công nghiệp, quyền đối với giống cây trồng theo quy định của Luật sở hữu trí tuệ</strong>: là toàn bộ các chi phí thực tế mà doanh nghiệp đã chi ra để có được quyền tác giả, quyền sở hữu công nghiệp, quyền đối với giống cây trồng theo quy định của pháp luật về sở hữu trí tuệ.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>g) Nguyên giá TSCĐ là các chương trình phần mềm:</strong></span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ của các chương trình phần mềm được xác định là toàn bộ các chi phí thực tế mà doanh nghiệp đã bỏ ra để có các chương trình phần mềm trong trường hợp chương trình phần mềm là một bộ phận có thể tách rời với phần cứng có liên quan, thiết kế bố trí mạch tích hợp bán dẫn theo quy định của pháp luật về sở hữu trí tuệ.</span></span></span></div>\r\n<h3 xss=removed><span xss=removed><span xss=removed><span xss=removed>3. Tài sản cố định thuê tài chính:</span></span></span></h3>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá TSCĐ thuê tài chính phản ánh ở đơn vị thuê là giá trị của tài sản thuê tại thời điểm khởi đầu thuê tài sản cộng ( ) với các chi phí trực tiếp phát sinh ban đầu liên quan đến hoạt động thuê tài chính.</span></span></span></div>\r\n<div xss=removed><br><span xss=removed><span xss=removed><span xss=removed><strong>Chú ý:</strong> Nguyên giá tài sản cố định của doanh nghiệp chỉ được thay đổi trong các trường hợp sau:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>a) Đánh giá lại giá trị TSCĐ trong các trường hợp:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Theo quyết định của cơ quan nhà nước có thẩm quyền.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Thực hiện tổ chức lại doanh nghiệp, chuyển đổi sở hữu doanh nghiệp, chuyển đổi hình thức doanh nghiệp: chia, tách, sáp nhập, hợp nhất, cổ phần hoá, bán, khoán, cho thuê, chuyển đổi công ty trách nhiệm hữu hạn thành công ty cổ phần, chuyển đổi công ty cổ phần thành công ty trách nhiệm hữu hạn.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Dùng tài sản để đầu tư ra ngoài doanh nghiệp.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>b) Đầu tư nâng cấp TSCĐ.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>c) Tháo dỡ một hay một số bộ phận của TSCĐ mà các bộ phận này được quản lý theo tiêu chuẩn của 1 TSCĐ hữu hình.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Khi thay đổi nguyên giá TSCĐ, doanh nghiệp phải lập biên bản ghi rõ các căn cứ thay đổi và xác định lại các chỉ tiêu nguyên giá, giá trị còn lại trên sổ kế toán, số khấu hao luỹ kế, thời gian sử dụng của TSCĐ và tiến hành hạch toán theo quy định.</span></span></span></div>', 1488635777, '["5"]', 'http://ketoanbanthoigian.com/images/san-pham/dich-vu-ke-toan-truong.jpg', 'Cách xác định nguyên giá của TSCĐ hữu hình, vô hình, thuê tài chính', 'Cách xác định nguyên giá của TSCĐ hữu hình, vô hình, thuê tài chính', 'Cách xác định nguyên giá của TSCĐ hữu hình, vô hình, thuê tài chínhQuy định mới nhất hiện nay về tài sản cố định là thông tư 45/2013/TT-BTC có hiệu lực từ ngày 10/6/2013. Để được gọi là ', 14, 0);
INSERT INTO `blogs` (`blog_id`, `blog_name`, `blog_content`, `blog_time`, `blog_cat_ids`, `blog_image`, `blog_seo_title`, `blog_seo_keyword`, `blog_seo_description`, `blog_user_id`, `blog_views`) VALUES
(114, 'Cách hạch toán tài sản cố định', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Khi doanh nghiệp mua tài sản về để phục vụ cho hoạt động sản xuất kinh doanh mà tài sản đó thỏa mãn được điều kiện là tài sản cố định: có giá trị từ 30 triệu đồng và có thời gian sử dụng trên 1 năm trở lên thì kế toán coi đó là tài sản cố định và hạch toán vào tài khoản 211.</span></span></span></h2>\r\n<div xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>( Theo điều 3 của TT 45/2013/TT-BTC, trước đây nguyên giá của TSCĐ xác định là từ 10 triệu)</span></span></span></strong></div>\r\n<div><span xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>Tài khoản 211 - Tài sản cố định hữu hình, có 6 tài khoản cấp 2:</strong></span></span></span></span></div>\r\n<div><span xss=removed><span xss=removed><span xss=removed><span xss=removed></span><br>- <strong>Tài khoản 2111</strong> - Nhà cửa vật kiến trúc: Phản ánh giá trị các công trình XDCB như nhà cửa, vật kiến trúc, hàng rào, bể, tháp nước, sân bãi, các công trình trang trí thiết kế cho nhà cửa, các công trình cơ sở hạ tầng như đường sá, cầu cống, đường sắt, cầu tàu, cầu cảng . . .<br>- <strong>Tài khoản 2112</strong> - Máy móc thiết bị: Phản ánh giá trị các loại máy móc, thiết bị dùng trong sản xuất, kinh doanh của doanh nghiệp bao gồm những máy móc chuyên dùng, máy móc, thiết bị công tác, dây chuyền công nghệ và những máy móc đơn lẻ.<br>- <strong>Tài khoản 2113</strong> - Phương tiện vận tải, truyền dẫn: Phản ánh giá trị các loại phương tiện vận tải, gồm phương tiện vận tải đường bộ, dắt, thuỷ, sông, hàng không, đường ống và các thiết bị truyền dẫn (Thông tin, điện nước, băng chuyền tải vật tư, hàng hoá).<br>- <strong>Tài khoản 2114</strong> - Thiết bị, dụng cụ quản lý: Phản ánh giá trị các loại thiết bị, dụng cụ sử dụng trong quản lý, kinh doanh, quản lý hành chính (Máy vi tính, quạt trần, quạt bàn, bàn ghế, thiết bị, dụng cụ đo lường, kiểm tra chất lượng hút ẩm hút bụi, chống mối mọt. . .).<br>- <strong>Tài khoản 2115 </strong>- Cây lâu năm, súc vật làm việc và cho sản phẩm: Phản ánh gái trị các loại TSCĐ là các loại cây lâu năm (Cà phê, chè, cao su, vườn cây ăn quả. . .) súc vật làm việc (Voi, bò, ngựa cày kéo. . .) và súc vật nuôi để lấy sản phẩm (Bò sữa, súc vật sinh sản. . .).<br>- <strong>Tài khoản 2118</strong> - TSCĐ khác: Phản ánh giá trị các loại TSCĐ khác chưa phản ánh ở các tài khoản nêu trên (Như tác phẩm nghệ thuật, sách chuyên môn kỹ thuật. . .).</span></span></span>\r\n<h3 xss=removed><span xss=removed><span xss=removed><span xss=removed>CÁCH HẠCH TOÁN CÁC NGHIỆP VỤ LIÊN QUAN ĐẾN TÀI SẢN CỐ ĐỊNH:</span></span></span></h3>\r\n<span xss=removed><span xss=removed><span xss=removed><strong>1. Kế toán tăng TSCĐ hữu hình:</strong></span></span></span><br xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>a). Mua sắm:</strong><br>- Căn cứ vào hóa đơn kế toán xác định nguyên giá của TSCĐ và hạch toán:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình (Giá mua chưa có thuế GTGT)<br>Nợ TK 133 - Thuế GTGT được khấu trừ (1332) ( Nếu có và được khấu trừ)<br>     Có các TK 111, 112, 311... </span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Nếu TSCĐ được mua sắm bằng nguồn vốn đầu tư XDCB hoặc quỹ đầu tư phát triển của doanh nghiệp dùng vào SXKD, kế toán phải ghi tăng nguồn vốn kinh doanh, giảm nguồn vốn XDCB hoặc giảm quỹ đầu tư phát triển khi quyết toán được duyệt, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 414 - Quỹ đầu tư phát triển<br>Nợ TK 441 - Nguồn vốn đầu tư XDCB<br>     Có TK 411- Nguồn vốn kinh doanh.</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed> - Trường hợp mua TSCĐ hữu hình theo phương thức trả chậm, trả góp:<br>  Khi mua TSCĐ hữu hình theo phương thức trả chậm, trả góp và đưa về sử dụng ngay cho XDCB, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình (Nguyên giá - ghi theo giá mua trả tiền ngay)<br>Nợ TK 133 - Thuế GTGT được khấu trừ (1332) (Nếu có)<br>Nợ TK 242 - Chi phí trả trước dài hạn [Phần lãi trả chậm là số chênh lệch giữa tổng số tiền phải thanh toán trừ (-) Giá mua trả tiền ngay trừ (-) Thuế GTGT (nếu có)]<br>     Có TK 331 - Phải trả cho người bán (Tổng giá thanh toán).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Định kỳ, thanh toán tiền cho người bán, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 331 - Phải trả cho người bán<br>     Có các TK 111, 112 (Số phải trả định kỳ gao gồm cả giá gốc và lãi trả chậm, trả góp phải trả từng kỳ).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Định kỳ, tính vào chi phí theo số lãi trả chậm, trả góp phải trả của từng kỳ, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 635 - Chi phí tài chính<br>     Có TK 242 - Chi phí trả trước dài hạn.</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed> - Trường hợp mua TSCĐ hữu hình là nhà cửa, vật kiến trức gắn liền với quyền sử dụng đất, đưa vào sử dụng ngay cho hoạt động SXKD, ghi:<br>Nợ TK 211 - TSCĐ hữu hình (Nguyên giá - chi tiết nhà cửa, vật kiến trúc)<br>Nợ TK 213 - TSCĐ vô hình (Nguyên giá - chi tiết quyền sử dụng đất)<br>Nợ TK 133 - Thuế GTGT được khấu trừ (Nếu có)<br>     Có các TK 111, 112, 331,. . .<br><strong>b). Trường hợp nhận vốn góp hoặc nhận vốn cấp bằng TSCĐ hữu hình</strong>, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình<br>     Có TK 411 - Nguồn vốn kinh doanh</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><strong>c. Trường hợp doanh nghiệp được tài trợ, biếu, tặng TSCĐ</strong> hữu hình đưa vào sử dụng ngay cho SXKD, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình<br>     Có TK 711 - Thu nhập khác.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Các chi phí khác liên quan trực tiếp đến TSCĐ hữu hình được tài trợ, biếu, tặng tính vào nguyên giá, ghi:<br>Nợ TK 211 - TSCĐ hữu hình<br>     Có các TK 111, 112, 331. . .</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><strong>d. Trường hợp TSCĐ hữu hình tự chế</strong>:<br>- Khi sử dụng sản phẩm do doanh nghiệp tự chế tạo để chuyển thành TSCĐ hữu hình sử dụng cho SXKD, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 632 - Giá vốn bán hàng<br>     Có TK 155 - Thành phẩm (Nếu xuất kho ra sử dụng)<br>     Có TK 154 - Chi phí SXKD dỡ dang (Nếu sản xuất xong đưa vào sử dụng ngay, không qua kho).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>  Đồng thời ghi tăng TSCĐ hữu hình:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình<br>     Có TK 512 - Doanh thu bán hàng nội bộ (Doanh thu là chi phí sản xuất thực tế sản phẩm).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Chi phí lắp đặt, chạy thử,. . . liên quan đến TSCĐ hữu hình, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình<br>     Có các TK 111, 112, 331. . .</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed> <br><strong>e). Trường hợp TSCĐ hữu hình tăng do đầu tư XDCB hoàn thành</strong>:<br>- Trường hợp quá trình đầu tư XDCB được hạch toán trên cùng hệ thống sổ kế toán của đơn vị:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Khi công tác XDCB hoàn thành nghiệm thu, bàn giao đưa tài sản vào sử dụng cho sản xuất, kinh doanh, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình (Nguyên giá)<br>     Có TK 241 - Xây dựng cơ bản dỡ dang.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Nếu tài sản hình thành qua đầu tư không thoả mãn các tiêu chuẩn ghi nhận TSCĐ hữu hình theo quy định của chuẩn mực kế toán TSCĐ hữu hình, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 152, 153 (Nếu là vật liệu, công cụ, dụng cụ nhập kho)<br>     Có TK 241 - XDCB dỡ dang.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Nếu doanh nghiệp sử dụng nguồn vốn đầu tư xây dựng cơ bản hoặc các quỹ doanh nghiệp để đầu tư XDCB, kế toán ghi tăng nguồn vốn kinh doanh, giảm nguồn vốn đầu tư XDCB và các quỹ doanh nghiệp khi quyết toán được duyệt.</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Trường hợp quá trình đầu tư XDCB không được hạch toán trên cùng hệ thống sổ kế toán của đơn vị (Đơn vị chủ đầu tư có tổ chức kế toán riêng để theo dõi quá trình đầu tư XDCB):</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Khi doanh nghiệp nhận bàn giao TSCĐ là sản phẩm đầu tư XDCB hoàn thành và nguồn vốn hình thành TSCĐ (Kể cả bàn giao khoản vay đầu tư XDCB), ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - Tài sản cố định hữu hình (Nguyên giá)<br>Nợ TK 133 - Thuế GTGT được khấu trừ (1332) (Nếu có khấu trừ)<br>     Có TK 411 - Nguồn vốn kinh doanh (Phần vốn chủ sở hữu)<br>     Có TK 341 - Vay dài hạn (Phần vốn vay các tổ chức tín dụng); hoặc<br>     Có TK 343 - Trái phiếu phát hành (Phần vốn vay trực tiếp do phát hành trái phiếu).<br>     Có TK 136 - Phải thu nội bộ (Phần vốn đơn vị cấp trên giao).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Đối với doanh nghiệp Nhà nước, nếu TSCĐ (Đầu tư qua nhiều năm) được nghiệm thu, bàn giao theo giá trị ở thời điểm bàn giao công trình (Theo giá trị phê duyệt của cấp có thẩm quyền), ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - Tài sản cố định hữu hình (Nguyên giá được duyệt)<br>     Có TK 411 - Nguồn vốn kinh doanh (Phần vốn chủ sở hữu)<br>     Có TK 341 - Vay dài hạn<br>     Có TK 136 - Phải thu nội bộ.</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><strong>f). Trường hợp công trình hoặc hạng mục công trình XDCB hoàn thành đã bàn giao đưa vào sử dụng</strong>, nhưng chưa được duyệt quyết toán vốn đầu tư, thì doanh nghiệp căn cứ vào chi phí đầu tư XDCB thực tế, tạm tính nguyên giá để hạch toán tăng TSCĐ (Để có cơ sở tính và trích khấu hao TSCĐ đưa vào sử dụng). Sau hi quyết toán vốn đầutư XDCB được duyệt, nếu có chênh lệch so với giá trị TSCĐ đã tạm tình thì kế toán thực hiện điều chỉnh tăng, giảm số chênh lệch.<br> <br><strong>g). Chi phí phát sinh sau ghi nhận ban đầu liên quan đến TSCĐ hữu hình như đã sửa chữa, cải tạo, nâng cấp:</strong><br>- Khi phát sinh chi phí sửa chữa, cải tạo, nâng cầp TSCĐ hữu hình sau khi ghi nhận ban đầu, ghi</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 241 - XDCB dỡ dang<br>Nợ TK 133 - Thuế GTGT được khấu trừ (1332)<br>     Có các TK 112, 152, 331, 334,. . .</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Khi công việc sửa chữa, cải tạo, nâng cập TSCĐ hoàn thành đưa vào sử dụng:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Nếu thoản mãn càc điều kiện được ghi tăng nguyên giá TSCĐ, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình<br>     Có TK 241 - Xây dựng cơ bảan dỡ dang.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Nếu không thoả mãn các điều kiện được ghi tăng nguyên giá TSCĐ hữu hình, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 623, 627, 641, 642 (Nếu giá trị nhỏ)<br>Nợ TK 242 - Chi phí trả trước dài hạn (Nếu giá trị lớn hơn phải phân bổ dần)<br>     Có TK 241 - Xây dựng cơ bản dỡ dang.</span></span></span></div>\r\n<h4 xss=removed><span xss=removed><span xss=removed><span xss=removed>2. Kế toán giảm TSCĐ hữu hình:</span></span></span></h4>\r\n<span xss=removed><span xss=removed><span xss=removed><strong>a). Nhượng bán TSCĐ</strong>: Khi nhượng bán TSCĐ hữu hình phải làm đầy đủ các thủ tục cần thiết (Lập Hội đồng xác định giá, thông báo công khai và tổ chức đấu giá, có hợp đồng mua bán, biên bản giao nhận TSCĐ. . .). Căn cứ vào biên bản giao nhận TSCĐ và hóa đơn nhượng bán TSCĐ:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 111, 112, 131,. . .<br>     Có TK 3331 - Thuế giá trị gia tăng phải nộp (33311) (Nếu DN kê khai thuế GTGT theo PP khấu trừ)<br>     Có TK 711 - Thu nhập khác (Giá bán chưa có thuế GTGT).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Căn cứ Biên bản giao nhận TSCĐ để ghi giảm TSCĐ đã nhượng bán:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 214 - Hao mòn TSCĐ (2141) (Giá trị đã hao mòn)<br>Nợ TK 811 - Chi phí khác (Giá trị còn lại)<br>     Có TK 211 - TSCĐ hữu hình (Nguyên giá).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Các chi phí phát sinh liên quan đến nhượng bán TSCĐ được phản ánh vào bên Nợ TK 811 “Chi phí khác”.</span></span></span><br xss=removed>\r\n<div xss=removed><span xss=removed>Nợ TK 811 - Chi phí khác (Giá trị còn lại)</span><br><span xss=removed>Nợ TK 1331 - </span><span xss=removed>Thuế GTGT được khấu trừ</span><span xss=removed> (N</span>ếu có <span xss=removed>)</span></div>\r\n<div xss=removed><span xss=removed>Có TK 111, 112, 331...</span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><strong>b). Trường hợp thanh lý TSCĐ</strong>: TSCĐ thanh lý là những TSCĐ hư hỏng, không thể tiếp tục sử dụng được, những TSCĐ lạc hậu về kỹ thuật hoặc không phù với yêu cầu sản xuất, kinh doanh. Khi có TSCĐ thanh lý, đơn vị phải ra quyết định thanh lý, thành lập Hội đồng thanh lý TSCĐ. Hội đồng thanh lý TSCĐ có nhiệm vụ tổ chức thực hiện việc thanh lý TSCĐ theo đúng trình tự, thủ tục quy định trong chế độ quản lý tài chính và lập “Biên bản thanh lý TSCĐ” theo mẫu quy định. Biên bản được lập thành 2 bản, 1 bản chuyển cho phòng kế toán để theo dõi ghi sổ, 1 bản giao cho đơn vị quản lý, sử dụng TSCĐ.<br>Căn cứ vào Biên bản thanh lý và các chứng từ có liên quan đến các khoản thu, chi thanh lý TSCĐ,. . . </span></span></span><span xss=removed><span xss=removed><span xss=removed><strong>kế toán ghi sổ như trường hợp nhượng bán TSCĐ.</strong></span></span></span><br xss=removed><span xss=removed><span xss=removed><span xss=removed> <br><strong>c). Góp vốn vào cơ sở kinh doanh đồng kiểm soát bằng TSCĐ hữu hình</strong>:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 222 - Vốn góp liên doanh (Theo giá trị do các bên liên doanh đánh giá)<br>Nợ TK 214 - Hao mòn TSCĐ (Số khấu hao đã trích)<br>Nợ TK 811 - Chi phí khác (Số chênh lệch giữa giá đánh giá lại nhỏ hơn giá trị còn lại của TSCĐ)<br>     Có TK 211 - TSCĐ hữu hình (Nguyên giá)<br>     Có TK 3387 - Doanh thu chưa thực hiện (Số chênh lệch giữa giá đánh giá lại lớn hơn giá giá trị còn lại của TSCĐ sẽ được hoãn lại phần chênh lệch tương ứng với phần lợi ích của mình trong liên doanh)<br>     Có TK 711 - Thu nhập khác (Số chênh lệch giữa giá đánh giá lại lớn hơn giá giá trị còn lại của TSCĐ tương ứng với phần lợi ích của các bên khác trong liên doanh).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>  Định kỳ, căn cứ vào thời gian sử dụng hữu ích của tài sản cồ định mà cơ sở kinh doanh đồng kiểm soát sử dụng, kế toán phân bổ doanh thu chưa thực hiện vào thu nhập khác trong kỳ, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 3387 - Doanh thu chưa thực hiện (Chi tiết chênh lệch do đánh giá lại TSCĐ đem đi góp vốn vào cơ sở kinh doanh đồng kiểm soát)<br>     Có TK 711 - Thu nhập khác (Phần doanh thu chưa thực hiện được phân bổ trong kỳ ).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed><strong>d. Kế toán TSCĐ hữu hình phát hiện thừa, thiếu</strong>:<br>Mọi trường hợp phát hiện thừa hoặc thiếu TSCĐ đều phải truy tìm nguyên nhân. Căn cứ vào “Biên bản kiểm kê TSCĐ” và kết luận của Hội đồng kiểm kê để hạch toán chính xác, kịp thời, theo từng nguyên nhân cụ thể:<br>- <strong>TSCĐ phát hiện thừa</strong>:<br>  Nếu TSCĐ phát hiện thừa do để ngoài sổ sách (chưa ghi sổ), kế toán phải căn cứ vào hồ sơ TSCĐ để ghi tăng TSCĐ theo từng trường hợp cụ thể:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 211 - TSCĐ hữu hình<br>     Có các TK 241, 331, 338, 411,. . .</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>  Nếu TSCĐ thừa đang sử dụng thì ngoài nghiệp vụ ghi tăng TSCĐ hữu hình, phải căn cứ cào nguyên giá và tỷ lệ khấu hao để xác định giá trị hao mòn làm căn cứ tính, trích bổ sung khấu hao TSCĐ hoặc trích bổ sung hao mòn đối với TSCĐ dùng cho hoạt động phúc lợi, sự nghiệp, dự án, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK Chi phí sản xuất, kinh doanh (TSCĐ dùng cho SXKD)<br>Nợ TK 4313 - Quỹ phúc lợi hình thành TSCĐ hữu hình (TSCĐ dùng cho mục đích phúc lợi)<br>Có TK 466 - Nguồn kinh phí đã hình thành TSCĐ (TSCĐ dùng cho hoạt động sự nghiệp, dự án)<br>     Có TK 241 - Hao mòn TSCĐ (2141).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>  Nếu TSCĐ phát hiện thừa được xác định là TSCĐ của đơn vị khác thì phải báo ngay cho đơn vị chủ tài sản đó biết. Nếu không xác định được đơn vị chủ tài sản thì phải báo ngay cho cơ quan cấp trên và cơ quan tài tài chính cùng cấp (nếu là DNNN) biết để xử lý. Trong thời gian chờ xử lý, kế toán phải căn cứ vào tài liệu kiểm kê, tạm thời phản ánh vào Tài khoản 002 “Vật tư, hàng hoá nhận giữ hộ, nhận gia công” (Tài khoản Cân đối kế toán) để theo dõi hộ.<br>- <strong>TSCĐ phát hiện thiếu</strong> phải được truy cứu nguyên hân, xác định người chịu trách nhiệm và xử lý theo chế độ tài chính hiện hành.<br>  Trường hợp có quyết định xử lý ngay: Căn cứ “Biên bản xử lý TSCĐ thiếu” đã được duyệt và hồ sơ TSCĐ, kế toán phải xác định chính xác nguyên giá, giá trị hao mòn của TSCĐ đó làm căn cứ ghi giảm TSCĐ và xử lý vật chất phần giá trị còn lại của TSCĐ. Tuỳ thuộc vào quyết định xử lý, ghi:<br>  Đối với TSCĐ thiếu dùng vào sản xuất, kinh doanh, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 214 - Hao mòn TSCĐ (Giá trị hao mòn)<br>Nợ các TK 111, 334, 138 (1388) (Nếu người có lỗi phải bồi thường)<br>Nợ TK 411 - Nguồn vốn kinh doanh (Nếu được phép ghi giảm vốn)<br>Nợ TK 811 - Chi phí khác (Nếu doanh nghiệp chịu tổn thất)<br>     Có TK 211 - TSCĐ hữu hình.</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>  Đối với TSCĐ thiếu dùng vào hoạt động sự nghiệp, dự án:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>(1) Phản ánh giảm TSCĐ, ghi:<br>Nợ TK 214 - Hao mòn TSCĐ (Giá trị hao mòn)<br>Có TK 466 - Nguồn kinh phí đã hình thành TSCĐ (Giá trị còn lại)<br>     Có TK 211 - TSCĐ hữu hình (Nguyên giá).<br>(2) Đối với phần giá trị còn lại của TSCĐ thiếu phải thu hồi theo quyết định xử lý, ghi:<br>Nợ TK 111 - Tiền mặt (Nếu thu tiền)<br>Nợ TK 334 -Phải trả người lao động (Nếu trừ vào lương người lao động)<br>     Có các TK liên quan (Tuỳ theo quyết định xử lý).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>  Đối với TSCĐ thiếu dùng vào hoạt động văn hóa, phúc lợi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>(1) Phản ánh giảm TSCĐ, ghi:<br>Nợ TK 214 - Hao mòn TSCĐ (Giá trị hao mòn)<br>Nợ TK 4313 - Quỹ phúc lợi đã hình thành TSCĐ (Giá trị còn lại)<br>     Có TK 211 - TSCĐ hữu hình (Nguyên giá).<br>(2) Đối với phần giá trị còn lại của TSCĐ thiếu phải thu hồi theo quyết định xử lý, ghi:<br>Nợ TK 111 - Tiền mặt (Nếu thu tiền)<br>Nợ TK 334 - Phải trả người lao động (Nếu trừ vào lương người lao động)<br>     Có TK 4312 - Quỹ phúc lợi.</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Trường hợp TSCĐ thiếu chưa xác định được nguyên nhân chờ xử lý:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>(1) Phản ánh giảm TSCĐ. Phần giá trị còn lại của TSCĐ thiếu, ghi:<br>Nợ TK 214 - Hao mòn TSCĐ (2141) (Giá trị hao mòn)<br>Nợ TK 138 - Phải thu khác (1381) (Giá trị còn lại)<br>     Có TK 211 - TSCĐ hữu hình (Nguyên giá).<br>(2) Khi có quyết định xử lý giá trị còn lại của TSCĐ thiếu, ghi:<br>Nợ TK 111 - Tiền mặt (Tiền bồi thường)<br>Nợ TK 138 - Phải thu khác (1381) (Nếu người có lỗi phải bồi thường)<br>Nợ TK 334 - Phải trả cho người lao động (Nếu trừ vào lương của người lao động)<br>Nợ TK 411 - Nguồn vốn kinh doanh (Nếu được phép ghi giảm vốn)<br>Nợ TK 811 - Chi phí khác (Nếu doanh nghiệp chịu tổn thất)<br>     Có TK 138 - Phải thu khác (1381).</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed> <br><strong>3. Đối với TSCĐ hữu hình dùng cho sản xuất, kinh doanh, nếu không đủ tiêu chuẩn ghi nhận theo quy định phải chuyển thành công cụ, dụng cụ ghi:</strong><br>Nợ các TK 623, 627, 642 (Nếu giá trị còn lại nhỏ)<br>Nợ TK 242 - Chi phí trả trước dài hạn (Nếu giá trị còn lại lớn phải phân bổ dần)<br>Nợ TK 214 - Hao mòn TSCĐ (Giá trị hao mòn)<br>     Có TK 211 - TSCĐ hữu hình (Nguyên giá TSCĐ).<br>4. Kế toán giao dịch bán và thuê lại TSCĐ hữu hình là thuê hoạt động.</span></span></span><br xss=removed><img src="/images/tin-tuc/cach_tinh_khau_hao_tai_san_co_dinh_theo_duong_thang.png" alt="cach tinh khau hao tai san co dinh theo duong thang" xss=removed><br xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>4.1. Trường hợp giao dịch bán và thuê lại TSCĐ với giá bán cao hơn giá trị còn lại của TSCĐ</strong>. Khi hoàn tất thủ tục bán tài sản cố định, căn cứ vào hoá đơn và các chứng từ liên quan, ghi:</span></span></span>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 111, 112, 131 (Tổng giá thanh toán)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 711 - Thu nhập khác (Theo giá trị còn lại của TSCĐ bán và thuê lại)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 3387 - Doanh thu chưa thực hiện (Chênh lệch giữa giá bán lớn hơn giá trị còn lại của TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 3331 – Thuế GTGT phải nộp.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Đồng thời, ghi giảm TSCĐ:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 811 - Chi phí khác (Giá trị còn lại của TSCĐ bán và thuê lại)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 214 - Hao mòn TSCĐ (Nếu có)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 211 - TSCĐ hữu hình (Nguyên giá TSCĐ).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Trường hợp giao dịch bán và thuê lại với giá thấp hơn giá trị còn lại của TSCĐ. Khi hoàn tất thủ tục bàn tài sản, căn cứ vào hoá đơn và các chứng từ liên quan, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 111, 112, 131 (Tổng giá thanh toán)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 711 - Thu nhập khác (Giá bán TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 3331 - Thuế GTGT phải nộp (Nếu có).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Đồng thời, ghi giảm TSCĐ:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 811 - Chi phí khác (Tính bằng giá bán TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 242 - Chi phí trả trước dài hạn (Chênh lệch giá bán nhỏ hơn giá trị còn lại của TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 214 - Hao mòn TSCĐ (Nếu có)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 211 - TSCĐ hữu hình (Nguyên giá TSCĐ).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Các bút toán ghi nhận tài sản thuê và nợ phải trả về thuê tài chính, trả tiền thuê từng kỳ thực hiện theo quy định tại TK 212 - Tài sản cố định thuê tài chính.</span></span></span></div>\r\n<div xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>4.2. Kế toán thu nhập khác phát sinh từ giao dịch bán và thuê lại TSCĐ là thuê hoạt động.</span></span></span></strong></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Khi bán TSCĐ và thuê lại, căn cứ vào Hoá đơn GTGT và các chứng từ liên quan đến việc bán TSCĐ, kế toán phản ánh giao dịch bán theo các trường hợp sau:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Nếu giá bán được thoả thuận ở mức giá trị hợp lý thì các khoản lỗ hay lãi phải được ghi nhận trong kỳ phát sinh. Phản ánh số thu nhập bán TSCĐ, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 111, 112, 131,. . .</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 711 - Thu nhập khác (Giá bán TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 3331 - Thuế GTGT phải nộp (Nếu có).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Đồng thời, ghi giảm TSCĐ (Như trường hợp 6)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Trường hợp gái bán và thuê lại TSCĐ thấp hơn giá trị hợp lý nhưng mức giá thuê thấp hơn giá thuê thị trường thì khoản lỗ này không được ghi nhận ngay mà phải phân bổ dần phù hợp với khoản thanh toán tiền thuê trong thời gian thuê tài sản. Căn cứ vào Hoá đơn GTGT và các chứng từ liên quan đến việc bán TSCĐ, phản ánh thu nhập bán TSCĐ, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 111, 112,. . .</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 711 - Thu nhập khác (Giá bán TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 3331 -Thuế GTGT phải nộp.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Đồng thời, ghi giảm TSCĐ (Như trường hợp 4.1)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>- Nếu giá bán và thuê lại tài sản cao hơn giá trị hợp lý thì khoản chênh lệch cao hơn giá trị hợp lý không được ghi nhận ngay là một khoản lãi trong kỳ mà được phân bổ dần trong suốt thời gian mà tài sản đó được dự kiến sử dụng, còn số chênh lệch giữa giá trị hợp lý và giá trị còn lại được ghi nhận ngay là một khoản lãi trong kỳ.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Căn cứ vào Hoá đơn GTGT bán TSCĐ, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ các TK 111, 112, 131,. . .</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 711 - Thu nhập khác (Tính bằng giá trị hợp lý của TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 3387 - Doanh thu chưa thực hiện (Chênh lệch giữa giá bán cao hơn giá trị hợp lý của TSCĐ)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có TK 3331 - Thuế GTGT phải nộp (Nếu có).</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Đồng thời, ghi giảm TSCĐ bán và thuê lại (Như trường hợp 6)</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Định kỳ, phân bổ chênh lệch giữa giá bán cao hơn giá trị hợp lý của TSCĐ bán và thuê lại ghi giảm chi phí sản xuất, kinh doanh trong kỳ phù hợp với khoản thanh toán tiền thuê trong suốt thời gian mà tài sản đó dự kiến sử dụng, ghi:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nợ TK 3387 - Doanh thu chưa thực hiện</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>     Có các TK 623, 627, 641, 642. </span></span></span></div>\r\n<div><img src="/images/tin-tuc/dich-vu-kiem-toan.jpg" alt="dich vu kiem toan" xss=removed></div>\r\n</div>', 1488635779, '["5"]', 'http://ketoanbanthoigian.com/images/tin-tuc/ketoantrongoi.jpg', 'Cách hạch toán tài sản cố định', 'Cách hạch toán tài sản cố định', 'Cách hạch toán tài sản cố địnhKhi doanh nghiệp mua tài sản về để phục vụ cho hoạt động sản xuất kinh doanh mà tài sản đó thỏa mãn được điều kiện là ', 14, 0),
(115, 'Các phương pháp trích khấu hao tài sản cố định', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Các phương pháp trích khấu hao tài sản cố định được quy định tại điều 13 của thông tư 45/2013/TT-BCT và các phương pháp trích hấu hao TSCĐ này được hướng dẫn cụ thể và chi tiết tại Phụ lục 2 của TT 45/2013/TT-BCT.</span></span></span></h2>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Theo đó doanh nghiệp có thể lựa chọn một trong các phương pháp trích khấu hao như sau:</span></span></span></p>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>1. Phương pháp khấu hao đường thẳng.<br>2. Phương pháp khấu hao theo số dư giảm dần có điều chỉnh.<br>3. Phương pháp khấu hao theo số lượng, khối lượng sản phẩm.</span></span></span></div>\r\n<p><img src="/images/images.jpg" alt="images" xss=removed><br xss=removed><span xss=removed><span xss=removed><span xss=removed>Nhiệm vụ của kế toán là phải lựa chọn được phương pháp khấu hao tài sản cố định sao cho phù tình hình hoạt động sản xuất kinh doanh của doanh nghiệp hay cụ thể hơn là phù hợp với tình hình sử dụng của từng loại tài sản cố định.<br><strong>Sau đây chúng tôi sẽ đưa ra một vài gợi ý cho các bạn như sau:</strong></span></span></span></p>\r\n<h4 xss=removed><span xss=removed><span xss=removed><span xss=removed>- Đối với phương pháp khấu hao đường thẳng:</span></span></span></h4>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Đây là phương pháp trích khấu hao theo mức tính ổn định từng năm vào chi phí sản xuất kinh doanh của doanh nghiệp của tài sản cố định tham gia vào hoạt động kinh doanh.<br>  Doanh nghiệp hoạt động có hiệu quả kinh tế cao được <strong>khấu hao nhanh</strong> nhưng tối đa không quá 2 lần mức khấu hao xác định theo phương pháp đường thẳng để nhanh chóng đổi mới công nghệ. Tài sản cố định tham gia vào hoạt động kinh doanh được trích khấu hao nhanh là máy móc, thiết bị; dụng cụ làm việc đo lường, thí nghiệm; thiết bị và phương tiện vận tải; dụng cụ quản lý; súc vật, vườn cây lâu năm. Khi thực hiện trích khấu hao nhanh, doanh nghiệp phải đảm bảo kinh doanh có lãi. Trường hợp doanh nghiệp trích khấu hao nhanh vượt 2 lần mức quy định tại khung thời gian sử dụng tài sản cố định nêu tại Phụ lục 1 kèm theo Thông tư 45/2013/TT-BCT, thì phần trích vượt mức khấu hao nhanh (quá 2 lần) không được tính vào chi phí hợp lý khi tính thuế thu nhập trong kỳ.<br>  Thông thường kế toán doanh nghiệp hay sử dụng phương pháp khấu hao này để áp dụng trích khấu hao bởi nó dễ theo dõi, dễ tính và phù hợp với các TSCĐ được sử dụng thường xuyên và liên tục.</span></span></span><br><span xss=removed>- Đối với phương pháp khấu hao theo số dư giảm dần có điều chỉnh:</span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Phương pháp này được áp dụng đối với các doanh nghiệp thuộc các lĩnh vực có công nghệ đòi hỏi phải thay đổi, phát triển nhanh.<br>  TSCĐ tham gia vào hoạt động kinh doanh được trích khấu hao theo phương pháp số dư giảm dần có điều chỉnh phải thoả mãn đồng thời các điều kiện sau:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>1. Tài sản cố định đầu tư mới (chưa qua sử dụng);<br>2. các loại máy móc, thiết bị; dụng cụ làm việc đo lường, thí nghiệm.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Chi tiết các trích khấu hao các bạn xem tại đây: <strong><a href="http://ketoanthienung.vn/phuong-phap-trich-khau-hao-tscd-theo-so-du-giam-dan-co-dieu-chinh.htm" target="_blank" type="phương pháp khấu hao theo số dư giảm dần có điều chỉnh" id="phương pháp khấu hao theo số dư giảm dần có điều chỉnh" xss=removed title="phương pháp khấu hao theo số dư giảm dần có điều chỉnh" name="phương pháp khấu hao theo số dư giảm dần có điều chỉnh">Phương pháp khấu hao theo số dư giảm dần có điều chỉnh</a></strong></span></span></span></div>\r\n<h4 xss=removed><span xss=removed><span xss=removed><span xss=removed>- Đối với phương pháp khấu hao theo số lượng, khối lượng sản phẩm:</span></span></span></h4>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  Tài sản cố định được trích khấu hao theo phương pháp này là các loại máy móc, thiết bị thỏa mãn đồng thời các điều kiện sau:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>1. Trực tiếp liên quan đến việc sản xuất sản phẩm;<br>2.  Xác định được tổng số lượng, khối lượng sản phẩm sản xuất theo công suất thiết kế của tài sản cố định;<br>3. Công suất sử dụng thực tế bình quân tháng trong năm tài chính không thấp hơn 100% công suất thiết kế.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Chi tiết các trích khấu hao các bạn xem tại đây: <strong><a href="http://ketoanthienung.vn/phuong-phap-trich-khau-hao-tscd-theo-san-luong-san-pham.htm" target="_blank" type="phương pháp trích khấu hao TSCĐ theo khối lượng, số lượng sản phẩm" id="phương pháp trích khấu hao TSCĐ theo khối lượng, số lượng sản phẩm" xss=removed title="phương pháp trích khấu hao TSCĐ theo khối lượng, số lượng sản phẩm" name="phương pháp trích khấu hao TSCĐ theo khối lượng, số lượng sản phẩm">Phương pháp khấu hao theo số lượng, khối lượng sản phẩm</a></strong></span></span></span></div>\r\n<div xss=removed><br><span xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>Chú ý:</strong></span><br>- Doanh nghiệp có quyền tự quyết định phương pháp trích khấu hao, thời gian trích khấu hao TSCĐ và thông báo với cơ quan thuế trực tiếp quản lý trước khi bắt đầu thực hiện.<br>- Phương pháp trích khấu hao áp dụng cho từng TSCĐ mà doanh nghiệp đã lựa chọn và thông báo cho cơ quan thuế trực tiếp quản lý phải được thực hiện nhất quán trong suốt quá trình sử dụng TSCĐ. Trường hợp đặc biệt cần thay đổi phương pháp trích khấu hao, doanh nghiệp phải giải trình rõ sự thay đổi về cách thức sử dụng TSCĐ để đem lại lợi ích kinh tế cho doanh nghiệp. Mỗi tài sản cố định chỉ được phép thay đổi một lần phương pháp trích khấu hao trong quá trình sử dụng và phải thông báo bằng văn bản cho cơ quan thuế quản lý trực tiếp.</span></span></span></div>', 1488635781, '["5"]', 'http://ketoanbanthoigian.com/images/tin-tuc/cong-viec-cua-ke-toan-tong-hop.jpg', 'Các phương pháp trích khấu hao tài sản cố định', 'Các phương pháp trích khấu hao tài sản cố định', 'Các phương pháp trích khấu hao tài sản cố địnhCác phương pháp trích khấu hao tài sản cố định được quy định tại điều 13 của thông tư 45/2013/TT-BCT và các phương pháp ', 14, 0);
INSERT INTO `blogs` (`blog_id`, `blog_name`, `blog_content`, `blog_time`, `blog_cat_ids`, `blog_image`, `blog_seo_title`, `blog_seo_keyword`, `blog_seo_description`, `blog_user_id`, `blog_views`) VALUES
(116, 'Cách kê khai hóa đơn hàng bán bị trả lại', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Đối với trường hợp hàng bán bị trả lại chúng ta có 2 đối tượng trả lại hàng đó là công ty và cá nhân mua hàng. </span></span></span></h2>\r\n<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>  Đối với trường hợp bán hàng cho công ty: Khi công ty đó muốn trả lại hàng thì công ty đó phải xuất hóa đơn trả lại hàng cho số hàng muốn trả.<br>  Còn đối với cá nhân mua hàng thì không có hóa đơn để xuất trả lại hàng nên trường hợp này công ty bán và người mua là cá nhân sẽ lập biên bản trả lại hàng, bên bán thu hồi hóa đơn đã xuất khi bán.</span></span></span></span></h2>\r\n<p>Sau đây, Ketoanbanthoigian sẽ hướng dẫn các bạn kế toán cách kê khai hàng bán bị trả lại trong từng trường hợp cụ thể:<br xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>1. Trường hợp bên mua hàng (bên trả lại hàng) là công ty:</strong></span></span></span><br xss=removed><span xss=removed><span xss=removed><span xss=removed>Ví dụ: <br>- Ngày 22/3/2016, Công ty cổ phần Hà Yến xuất hóa đơn bán 5 Bộ điều hòa cho công ty Thiên Ưng.<br>- Ngày 05/04/2016, Công ty Thiên Ưng phát hiện ra điều hòa không đúng chủng loại như đã thỏa thuận nên trả lại hàng. Để trả lại hàng đã nhận vào ngày 22/3, Công ty xuất hóa đơn trả lại hàng như sau:</span></span></span><br xss=removed><br xss=removed></p>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed><img src="http://ketoanthienung.vn/pic/News/images/hoa-don-tra-lai-hang.png" alt="mẫu hóa đơn trả lại hàng" id="mẫu hóa đơn trả lại hàng" xss=removed title="mẫu hóa đơn trả lại hàng"></span></span></span></div>\r\n<div xss=removed><br><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Hóa đơn trả lại hàng trên sẽ là căn cứ để Công ty Hà Yến điều chỉnh doanh thu và thuế GTGT đầu ra, còn Thiên Ưng sẽ điều chỉnh hàng hóa và thuế GTGT đầu vào.</span></span></span></span>\r\n<p><strong>1.1. Kỳ kê khai</strong>: Căn cứ hóa đơn xuất trả lại hàng, Công ty thực hiện kê khai tại kỳ tính thuế phát sinh hóa đơn trả lại hàng hoặc hóa đơn điều chỉnh (Theo Công văn số 2441/CT-HTr ngày 15/1/2016 của Cục Thuế TP. Hà Nội)<br>Trong ví dụ trên thì hóa đơn mẫu bên trên sẽ được công ty bán (nhận hàng trả lại) và công ty mua (trả lại hàng) kê khai vào tháng 4 (Nếu kê khai theo tháng) hoặc Qúy 2/2016 (Nếu kê khai theo quý).<br><strong>1.2. Cách kê khai trên tờ khai thuế GTGT trên phần mềm HTKK</strong>:<br>Công văn 4943/TCT-KK Ngày 23/11/2015 của Tổng Cục Thuế ban hành thì cả hai công ty thực hiện kê khai điều chỉnh giảm vào kỳ phát sinh hóa đơn trả lại hàng:</p>\r\n<table border="1" xss=removed cellspacing="1" cellpadding="1">\r\n<tbody>\r\n<tr>\r\n<td xss=removed><span xss=removed><span xss=removed>Bên Bán - Bị Trả lại hàng<br>(Nhận hóa đơn trả lại hàng - Liên 2)</span></span></td>\r\n<td xss=removed><span xss=removed><span xss=removed>Bên Mua - Trả lại hàng<br>(Xuất hóa đơn trả lại)</span></span></td>\r\n</tr>\r\n<tr>\r\n<td><span xss=removed><span xss=removed>- Kê âm tại bảng kê bán ra 01-1/GTGT</span></span></td>\r\n<td><span xss=removed><span xss=removed>- Kê âm tại bảng kê mua vào 01-2/GTGT</span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><span xss=removed><span xss=removed><span xss=removed><span xss=removed><strong>Chú ý</strong></span>:<br>- Kê khai âm bằng cách đặt dấu trừ đằng trước giá trị. (Sau khi các bạn đặt dấu trừ đằng trước giá trị, rồi ấn tab hoặc chuyển ô là phần mềm tự động đưa giá trị vào trong ngoặc - hiểu là âm)<br>- Nếu bạn không làm phụ tờ khai thì trừ trực tiếp trên tờ khai 01/GTGT theo mức suất tương ứng với thuế suất trên hóa đơn trả lại hàng.<br>- Nếu trả lại hàng phát sinh cùng kỳ với khi bán hàng (Ví dụ: mua  - bán ngày 3/3/2016 trả lại ngày 5/3/216 - Cùng kỳ tháng 3 hoặc quý 1 năm 2016) thì có thể lựa chọn 1 trong 2 cách sau:<br>  Cách 1: Kê dương hóa đơn khi bán - Khi mua RỒI LẠI kê âm hóa đơn trả lại hàng <br>  Cách 2: Không kê khai cả 2 hóa đơn trên vì thực tế nếu kê thì dương với âm triệt tiêu nhau hết. mà bây giờ nộp tờ khai không có phụ lục thì kê khai 1 dương 1 âm hay không kê cũng như nhau.</span></span></span></p>\r\n<p><span xss=removed><span xss=removed><span xss=removed><strong>2. Trường hợp bên mua hàng (bên trả lại hàng) là cá nhân:</strong></span></span></span></p>\r\n<div><span xss=removed><span xss=removed><span xss=removed>Theo Điểm 2.8 Phụ lục 4 của thông tư 39/2014/TT-BTC thì:</span></span></span></div>\r\n<div><span xss=removed><span xss=removed><span xss=removed>Trường hợp người mua là đối tượng không có hóa đơn, khi trả lại hàng hóa, bên mua và bên bán phải lập biên bản ghi rõ loại hàng hóa, số lượng, giá trị hàng trả lại theo giá không có thuế GTGT, tiền thuế GTGT theo hóa đơn bán hàng (số ký hiệu, ngày, tháng của hóa đơn), lý do trả hàng và bên bán thu hồi hóa đơn đã lập</span></span></span></div>\r\n</div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>* Đối với trường hợp trả lại toàn bộ hàng hóa:</span></span></span></div>\r\n<div xss=removed>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Khi trả lại hàng hóa, bên mua và bên bán phải lập biên bản ghi rõ loại hàng hóa, số lượng, giá trị hàng trả lại theo giá không có thuế GTGT, tiền thuế GTGT theo hóa đơn bán hàng (số ký hiệu, ngày, tháng của hóa đơn), lý do trả hàng và bên bán thu hồi hóa đơn đã lập.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>* Đối với trường hợp trả lại một phần hàng hóa:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Sau khi lập biên bản thu hồi hóa đơn như trên và nhận số hàng bị trả lại, bên bán lập hóa đơn mới cho hàng hóa thực tế bên mua chấp nhận theo quy định.</span></span></span></div>\r\n<span xss=removed><span xss=removed><span xss=removed>- Nếu chưa kê khai hóa đơn lúc bán thì bên bán không cần phải kê khai hóa đơn này nữa.<br>- Nếu đã kê khai thuế của hóa đơn đầu ra, sau đó người mua trả lại toàn bộ hay một phần hàng và hóa đơn GTGT thì hai bên lập biên bản thu hồi các liên của số hóa đơn của hàng hóa bị trả lại, lưu giữ hóa đơn tại người bán. Biên bản thu hồi hóa đơn phải thể hiện được lý do trả lại hàng hóa và các thỏa thuận bồi thường giữa hai bên (nếu có). Căn cứ vào hóa đơn người bán trả lại, Công ty thực hiện điều chỉnh giảm doanh thu hàng hóa dịch vụ bán ra chịu thuế GTGT và thuế GTGT đầu ra trên tờ khai thuế GTGT mẫu số 01/GTGT. </span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>(<em>Theo Công văn số 84288/CT-HTr ngày 29/12/2015 của Cục Thuế TP. Hà Nội về chính sách thuế trả lời vướng mắc liên quan đến cách xử lý hóa đơn khi người mua trả lại hàng hóa</em>)</span></span></span></div>', 1488635782, '["5"]', 'http://ketoanbanthoigian.com/images/dich-vu/hop-tac-2.jpg', 'Cách kê khai hóa đơn hàng bán bị trả lại', 'Cách kê khai hóa đơn hàng bán bị trả lại', 'Cách kê khai hóa đơn hàng bán bị trả lạiĐối với trường hợp hàng bán bị trả lại chúng ta có 2 đối tượng trả lại hàng đó là công ty và cá nhân mua hàng. \r\n  Đ', 14, 0),
(117, 'Thuế suất thuế thu nhập doanh nghiệp 2016 mới nhất hiện hành', '<div class="name1" xss=removed><span xss=removed></span>\r\n<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Mức thuế suất thuế TNDN 2016 được quy định tại điều 11 của TT 78/2014/TT-BTC theo đó năm 2016 mức thuế suất thuế TNDN là 20%</span></span></span></h2>\r\n</div>\r\n<div class="contentNew" xss=removed>\r\n<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed><img src="http://ketoanthienung.vn/pic/News/images/thue-suat-thue-TNDN(3).jpg" alt="Thuế suất thuế TNDN năm 2016" id="Thuế suất thuế TNDN năm 2016" xss=removed title="Thuế suất thuế TNDN năm 2016"></span></span></span></h2>\r\n<div><span xss=removed><span xss=removed><span xss=removed>Với năm 2015 thì có 2 mức thuế suất cơ bản:</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  <strong>Thuế suất 20%</strong>: Dành cho các DN có tổng doanh thu năm trước liền kề không quá 20 tỷ.</span></span></span></div>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>  <strong>Thuế suất 22%</strong>: dành cho các DN có tổng DT năm trước liền kề lớp hơn 20 tỷ.</span></span></span></div>\r\n<div><br><strong><span xss=removed><span xss=removed><span xss=removed>Nhưng theo khoản 1 điều 11 của thông tư 78/2014/TT-BTC thì Kể từ ngày 01/01/2016, trường hợp thuộc diện áp dụng thuế suất 22% chuyển sang áp dụng thuế suất 20%.</span></span></span></strong></div>\r\n<div><span xss=removed><strong><span xss=removed><span xss=removed>=', 1488635786, '["5"]', 'http://ketoanbanthoigian.com/images/Dich_vu_ke_toan_tai_nha3.jpg', 'Thuế suất thuế thu nhập doanh nghiệp 2016 mới nhất hiện hành', 'Thuế suất thuế thu nhập doanh nghiệp 2016 mới nhất hiện hành', 'Thuế suất thuế thu nhập doanh nghiệp 2016 mới nhất hiện hành\r\nMức thuế suất thuế TNDN 2016 được quy định tại điều 11 của TT 78/2014/TT-BTC theo đó năm 2016 mức thuế suất thuế TNDN là 20', 14, 0),
(118, 'Các phương pháp để tính giá hàng tồn kho', '<p><span xss=removed>Hàng tồn kho trong các doanh nghiệp tăng từ nhiều nguồn khác nhau với các đơn giá khác nhau, vì vậy, tùy thuộc vào loại hàng hóa mà doanh nghiệp cần lựa chọn phương pháp tính giá hàng xuất kho hợp lý. Ngoài ra, việc lựa chọn phương pháp tính giá hàng tồn kho nào còn tuỳ thuộc vào đặc điểm của loại hàng tồn kho, yêu cầu đối với măt hàng đó và trình độ quản lý của doanh nghiệp.</span></p>\r\n<div class="contentNew" xss=removed><span xss=removed><span xss=removed><span xss=removed><img src="http://ketoanthienung.vn/pic/News/images/cac-phuong-phap-tinh-gia-xuat-kho.jpg" alt="các phưowng pháp hạch toán hàng tồn kho" id="các phưowng pháp hạch toán hàng tồn kho" xss=removed title="các phưowng pháp hạch toán hàng tồn kho"><br>Chú ý: việc chọn và sử dụng phương pháp tính giá hàng tồn kho phải được nhất quán trong suốt niên độ kế toán, nếu có vấn đề gì thay đổi thì cần phải có sự giải trình cụ thể và hợp lý. Để tính giá hàng tồn kho xuất, kế toán viên có thể áp dụng một trong các phương pháp sau đây:</span></span></span><br>\r\n<h3 xss=removed><span xss=removed><span xss=removed><span xss=removed><em>1. Phương pháp giá thực tế đích danh</em></span></span></span></h3>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed>Theo phương pháp này sản phẩm, vật tư, hàng hoá xuất kho thuộc lô hàng nhập nào thì lấy đơn giá nhập kho của lô hàng đó để tính. Đây là phương án tốt nhất, nó tuân thủ nguyên tắc phù hợp của kế toán; chi phí thực tế phù hợp với doanh thu thực tế. Giá trị của hàng xuất kho đem bán phù hợp với doanh thu mà nó tạo ra. Hơn nữa, giá trị hàng tồn kho được phản ánh đúng theo giá trị thực tế của nó.</span></span></span></span>\r\n<p>Tuy nhiên, việc áp dụng phương pháp này đòi hỏi những điều kiện khắt khe, chỉ những doanh nghiệp kinh doanh có ít loại mặt hàng, hàng tồn kho có giá trị lớn, mặt hàng ổn định và loại hàng tồn kho nhận diện được thì mới có thể áp dụng được phương pháp này. Còn đối với những doanh nghiệp có nhiều loại hàng thì không thể áp dụng được phương pháp này.</p>\r\n<h3 xss=removed><span xss=removed><span xss=removed><span xss=removed><em>2. Phương pháp tính giá bình quân</em></span></span></span></h3>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Theo phương pháp này giá trị của từng loại hàng tồn kho được tính theo giá trị trung bình của từng loại hàng tồn kho đầu kỳ và giá trị từng loại hàng tồn kho được mua hoặc sản xuất trong kỳ. Phương pháp bình quân có thể được tính theo thời kỳ hoặc vào mỗi khi nhập một lô hàng, phụ thuộc vào tình hình của doanh nghiệp.<br><em>a) Theo giá bình quân gia quyền cuối kỳ (tháng) (giá bình quan cả kỳ dự trữ)</em><br> <br>Theo phương pháp này, đến cuối kỳ hàng xuất kho mới được tính giá trị. Tuỳ theo kỳ dự trữ của doanh nghiệp áp dụng mà kế toán hàng tồn kho căn cứ vào <strong>giá nhập, lượng hàng tồn kho đầu kỳ và nhập trong kỳ</strong> để tính giá đơn vị bình quân:</span></span></span></span></span></span></span></div>\r\n<div> </div>\r\n<div class="contentNew" xss=removed><strong><em><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Dưới đây là cách tính bình quân gia quyền:<br></span></span></span></span></em></strong>\r\n<table border="1" xss=removed cellspacing="1" cellpadding="1">\r\n<tbody>\r\n<tr>\r\n<td xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>Đơn giá xuất kho bình quân trong kỳ của một loại sản phẩm</span></span></span></strong></td>\r\n<td xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>=</span></span></span></strong></td>\r\n<td xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>(Giá trị hàng tồn đầu kỳ   Giá trị hàng nhập trong kỳ)<br>--------------------------------------------------------------------<br>(Số lượng hàng tồn đầu kỳ   Số lượng hàng nhập trong kỳ)</span></span></span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed> <br>-  Ưu điểm: Đơn giản, dễ làm, chỉ cần tính toán một lần vào cuối kỳ.<br>-  Nhược điểm: Độ chính xác không cao, thêm vào đó, việc tính toán dồn vào cuối tháng sẽ gây ảnh hưởng đến tiến độ của các công việc khác. Ngoài ra, phương pháp này không đáp ứng yêu cầu kịp thời của thông tin kế toán ngay tại thời điểm phát sinh nghiệp vụ.<br> <br><em>b) Theo giá bình quân gia quyền sau mỗi lần nhập (bình quân thời điểm, tức thời)</em><br> <br>Sau mỗi lần nhập sản phẩm, vật tư, hàng hoá, kế toán phải xác định lại giá trị thực của hàng tồn kho và giá đơn vị bình quân. </span></span></span></span></span></span></span></div>\r\n<div> </div>\r\n<div class="contentNew" xss=removed><em><strong><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Giá đơn vị bình quân được tính theo công thức sau:</span></span></span><br></span></span></span></span></strong></em>\r\n<table border="1" xss=removed cellspacing="1" cellpadding="1">\r\n<tbody>\r\n<tr>\r\n<td xss=removed><span xss=removed><strong><span xss=removed><span xss=removed>Đơn giá xuất kho lần thứ i</span></span></strong></span></td>\r\n<td xss=removed><span xss=removed><strong><span xss=removed><span xss=removed>=</span></span></strong></span></td>\r\n<td xss=removed><span xss=removed><strong><span xss=removed><span xss=removed>(Trị giá vật tư hàng hóa tồn đầu kỳ   Trị giá vật tư hàng hóa nhập trước lần xuất thứ i)<br>------------------------------------------------------------------------------------------------------------------<br>(Số lượng vật tư hàng hóa tồn đầu kỳ   Số lượng vật tư, HH nhập trước lần xuất thứ i)</span></span></strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed> <br>Phương pháp này có ưu điểm là khắc phục được những hạn chế của phương pháp trên nhưng việc tính toán phức tạp, nhiều lần, tốn nhiều công sức. Vì thế, phương pháp này thường được áp dụng ở các doanh nghiệp có ít chủng loại hàng tồn kho, có lưu lượng nhập xuất ít.<br><em>c)  Theo giá bình quân gia quyền cuối kỳ trước:</em><br>Dựa vào trị giá và số lượng hàng tồn kho cuối kỳ trước, kế toán tính giá đơn vị bình quân cuối kỳ trước để tính giá xuất.</span></span></span><br></span></span></span></span>\r\n<table border="1" xss=removed cellspacing="1" cellpadding="1">\r\n<tbody>\r\n<tr>\r\n<td xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>Giá đơn vị bình quân cuối kỳ trước</span></span></span></strong></td>\r\n<td xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>=</span></span></span></strong></td>\r\n<td xss=removed><strong><span xss=removed><span xss=removed><span xss=removed>Trị giá thực tế vật tư, SP, hàng hoá tồn kho cuối kỳ trước<br>----------------------------------------------------------------------------<br>Số lượng vật tư, SP, hàng hoá thực tế tồn kho cuối kỳ trước</span></span></span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><br><span xss=removed><span xss=removed><span xss=removed>Theo chúng tôi, phương pháp này có ưu điểm đơn giản, dễ tính toán nhưng trị giá hàng xuất không chịu ảnh hưởng của sự thay đổi giá cả trong kỳ hiện tại. Vì vậy, phương pháp này làm cho chỉ tiêu trên báo cáo kết quả kinh doanh không sát với giá thực tế.</span></span></span><br></span></span></span></span>\r\n<h3 xss=removed><span xss=removed><span xss=removed><span xss=removed><em>3. Phương pháp FIFO: First in - First out (nhập trước xuất trước)</em></span></span></span></h3>\r\n<span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed><span xss=removed>Phương pháp này áp dụng dựa trên giả định là hàng được mua trước hoặc sản xuất trước thì được xuất trước và giá trị hàng xuất kho được tính theo giá của lô hàng nhập trước hoặc sản xuất trước và thực hiện tuần tự cho đến khi chúng được xuất ra hết.<br>-  Phương pháp này giúp cho chúng ta có thể tính được ngay trị giá vốn hàng xuất kho từng lần xuất hàng, do vậy đảm bảo cung cấp số liệu kịp thời cho kế toán ghi chép các khâu tiếp theo cũng như cho quản lý. Trị giá vốn của hàng tồn kho sẽ tương đối sát với giá thị trường của mặt hàng đó. Vì vậy chỉ tiêu hàng tồn kho trên báo cáo kế toán có ý nghĩa thực tế hơn.<br>-  Tuy nhiên, phương pháp này có nhược điểm là làm cho doanh thu hiện tại không phù hợp với những khoản chi phí hiện tại. Theo phương pháp này, doanh thu hiện tại được tạo ra bởi giá trị sản phẩm, vật tư, hàng hoá đã có được từ cách đó rất lâu. Đồng thời nếu số lượng chủng loại mặt hàng nhiều, phát sinh nhập xuất liên tục dẫn đến những chi phí cho việc hạch toán cũng như khối lượng công việc sẽ tăng lên rất nhiều.</span></span></span><br></span></span></span></span>\r\n<h4 xss=removed><span xss=removed><span xss=removed><span xss=removed><em>4. Phương pháp LIFO: Last in - First out (nhập sa\n\n&lt;!-- JoomlaWorks "Simple Image Gallery Pro" Plugin (v3.0.8) starts here --&gt;\n<ul id="sigProIda672ebb4e0" class="sigProContainer sigProClassic">\n<li class="sigProThumb">\n<span class="sigProLinkOuterWrapper">\n<span class="sigProLinkWrapper">\n&lt;a href="/images/tin-tuc/accounting.jpg" class="sigProLink fancybox-button" style="width:200px;height:160px;" rel="fancybox-button[gallerya672ebb4e0]"', 1488635788, '["5"]', 'http://ketoanbanthoigian.com/images/hinh-anh/images_3.jpg', 'Các phương pháp để tính giá hàng tồn kho', 'Các phương pháp để tính giá hàng tồn kho', 'Các phương pháp để tính giá hàng tồn khoHàng tồn kho trong các doanh nghiệp tăng từ nhiều nguồn khác nhau với các đơn giá khác nhau, vì vậy, tùy thuộc vào loại hàng hóa', 14, 0),
(119, 'Cách sử dụng hóa đơn cho Chi Nhánh - Quy định sử dụng hóa đơn Chi Nhánh', '<p>Về việc kê khai thuế GTGT và sử dụng hóa đơn tại các chi nhánh, địa điểm kinh doanh</p>\r\n<p>Căn cứ:<br>– Điểm c, d, khoản 1, Điều 11, Thông tư số 156/2013/TT-BTC<br>– Điều 2, khoản 5 và Điều 3, Thông tư 26/2015/TT-BTC</p>\r\n<p>Theo đó:</p>\r\n<p>Về khai, nộp thuế GTGT của các chi nhánh, địa điểm kinh doanh của Công ty</p>\r\n<p>Về vấn đề này, Tham khảo công văn số 805/TCT-KK</p>\r\n<p>Về hóa đơn sử dụng tại các chi nhánh, địa điểm kinh doanh của Công ty</p>\r\n<p>Kể từ khi các chi nhánh, địa điểm kinh doanh được cấp mã số thuế theo quy định thì Công ty thực hiện phát hành hóa đơn cho chi nhánh, địa điểm kinh doanh theo quy định tại Điều 9 Thông tư số 39/2014/TT-BTC, cụ thể:</p>\r\n<p>– Trường hợp có sự thay đổi địa chỉ kinh doanh, thay đổi cơ quan thuế quản lý trực tiếp, nếu công ty có nhu cầu tiếp tục sử dụng số hóa đơn đã phát hành chưa sử dụng hết thì:</p>\r\n<p>  Phải nộp báo cáo tình hình sử dụng hóa đơn với cơ quan thuế quản lý trực tiếp;<br>  Đóng dấu địa chỉ mới lên hóa đơn, gửi bảng kê hóa đơn chưa sử dụng (mẫu số 3.10 Phụ lục 3 ban hành kèm theo Thông tư số 39/2014/TT-BTC);<br>  Thông báo điều chỉnh thông tin tại thông báo phát hành hóa đơn đến cơ quan thuế quản lý trực tiếp của chi nhánh (trong đó nêu rõ số hóa đơn đã phát hành chưa sử dụng, sẽ tiếp tục sử dụng).</p>\r\n<p>– Nếu công ty không có nhu cầu sử dụng số hóa đơn đã phát hành nhưng chưa sử dụng hết thì:<br>  Thực hiện hủy các số hóa đơn chưa sử dụng;<br>  Thông báo kết quả hủy hóa đơn với cơ quan thuế quản lý trực tiếp;<br>  Thực hiện thông báo phát hành hóa đơn mới với cơ quan thuế quản lý trực tiếp của chi nhánh.</p>\r\n<p>– Trường hợp có sự thay đổi về nội dung đã thông báo phát hành, công ty phải thực hiện thông báo phát hành mới theo hướng dẫn tại khoản 2, Điều 9, Thông tư số 39/2014/TT-BTC).</p>\r\n<p>Nguồn tham khảo: <a href="https://drive.google.com/drive/folders/0B9rCYsblAeOVSjVwN0wyWXZmS3c">Công văn 3063/TCT-KK, ngày 08/07/2016</a></p>', 1488635789, '["5"]', 'http://ketoanbanthoigian.com/images/hinh-anh/Untitled.png', 'Cách sử dụng hóa đơn cho Chi Nhánh - Quy định sử dụng hóa đơn Chi Nhánh', 'Cách sử dụng hóa đơn cho Chi Nhánh - Quy định sử dụng hóa đơn Chi Nhánh', 'Cách sử dụng hóa đơn cho Chi Nhánh - Quy định sử dụng hóa đơn Chi NhánhVề việc kê khai thuế GTGT và sử dụng hóa đơn tại các chi nhánh, địa điểm kinh doanh\r\nCăn cứ:– Điểm c, d, khoản 1, Điều 11,', 14, 0),
(120, 'Cách trích khấu hao tài sản cố định theo phương pháp đường thẳng', '<h2 xss=removed><span xss=removed><span xss=removed><span xss=removed>Trích khấu hao tài sản cố định theo phương pháp đường thẳng là phương pháp trích khấu hao theo mức tính ổn định từng năm vào chi phí sản xuất kinh doanh của doanh nghiệp của tài sản cố định tham gia vào hoạt động kinh doanh, thường áp dụng với máy móc, thiết bị..</span></span></span></h2>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Công thức tính mức trích khấu hao theo năm như sau:<br><img src="http://ketoanthienung.vn/pic/News/images/trich-khau-hao-duong-thang.png" alt="cách tính trích khấu hao tscđ theo năm" id="cách tính trích khấu hao tscđ theo năm" xss=removed title="cách tính trích khấu hao tscđ theo năm"><br>Mức trích khấu hao trung bình hàng tháng bằng số khấu hao phải trích cả năm chia cho 12 tháng.<br>Để xác định được thời gian trích khấu hao của TSCĐ các bạn phải dựa vào khung khấu hao tài sản cố định trong phụ lục của thông tư số 45/2013/TT-BTC. <strong>Chi tiết xem tại đây:</strong> </span></span></span><a href="http://www.ketoanthienung.vn/khung-thoi-gian-khau-hao-cac-loai-tai-san-co-dinh-moi-nhat.htm" type="khung thời gian khấu hao tài sản cố định" id="khung thời gian khấu hao tài sản cố định" xss=removed title="khung thời gian khấu hao tài sản cố định" name="khung thời gian khấu hao tài sản cố định"><span xss=removed><span xss=removed><span xss=removed><strong>Khung thời gian khấu hao tài sản cố định</strong></span></span></span></a><span xss=removed><span xss=removed><span xss=removed><a href="http://www.ketoanthienung.vn/khung-thoi-gian-khau-hao-cac-loai-tai-san-co-dinh-moi-nhat.htm" type="khung thời gian khấu hao tài sản cố định" title="khung thời gian khấu hao tài sản cố định" name="khung thời gian khấu hao tài sản cố định">.</a><br>- Trường hợp thời gian trích khấu hao hay nguyên giá của tài sản cố định thay đổi, doanh nghiệp phải xác định lại mức trích khấu hao trung bình của tài sản cố định bằng cách lấy giá trị còn lại trên sổ kế toán chia (:) cho thời gian trích khấu hao xác định lại hoặc thời gian trích khấu hao còn lại (được xác định là chênh lệch giữa thời gian trích khấu hao đã đăng ký trừ thời gian đã trích khấu hao) của tài sản cố định.<br>-  Mức trích khấu hao cho năm cuối cùng của thời gian trích khấu hao tài sản cố định được xác định là hiệu số giữa nguyên giá tài sản cố định và số khấu hao luỹ kế đã thực hiện đến năm trước năm cuối cùng của tài sản cố định đó.<br><span xss=removed><em><strong>Chú ý:</strong></em> </span>Đối với tài sản cố định các bạn đưa vào sử dụng từ ngày nào thì chúng ta được trích khấu hao đưa vào chi phí từ ngày đó. Vì vậy để lấy chính xác chi phí khấu hao tài sản cố định các bạn tách ra số ngày sử dụng thực tế trong tháng. </span></span></span><br xss=removed><img src="http://ketoanthienung.vn/pic/News/images/cach-trich-khao-hao-tscd.png" alt="cách trích khấu hao theo phương pháp đường thẳng" id="cách trích khấu hao theo phương pháp đường thẳng" xss=removed title="cách trích khấu hao theo phương pháp đường thẳng"></p>\r\n<h4 xss=removed><span xss=removed><span xss=removed><span xss=removed>Sau đây Kế Toán Thiên Ưng sẽ lấy 1 ví dụ cụ thể để các bạn có thể hình dung rõ cách tính khấu hao TSCĐ theo phương pháp đường thẳng:</span></span></span></h4>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Công ty Kế Toán Thiên Ưng mua một máy Photo (mới 100%) với giá ghi trên hoá đơn là 45 triệu đồng, chi phí vận chuyển là 2 triệu đồng, chi phí lắp đặt, chạy thử là 1 triệu đồng.<br>Biết rằng:<br>- Máy Photo có tuổi thọ kỹ thuật là 5 năm.<br>- Thời gian trích khấu hao của máy Photo Công ty Kế Toán Thiên Ưng dự kiến là 8 năm (phù hợp với quy định tại Phụ lục 1 ban hành kèm theo Thông tư số 45/2013/TT- BTC là từ 7 - 15 năm), máy Photo được đưa vào sử dụng vào ngày 1/1/2014.<br>Chúng ta tiến hành xác định như sau:</span></span></span></p>\r\n<div xss=removed><span xss=removed><span xss=removed><span xss=removed>Nguyên giá tài sản cố định = 45 triệu   2 triệu   1 triệu = 48 triệu đồng<br>Mức trích khấu hao trung bình hàng năm = 48 triệu : 8 năm = 6 triệu đồng/năm.<br>Mức trích khấu hao trung bình hàng tháng = 6 triệu đồng : 12 tháng = 0,5 triệu đồng/ tháng<br>Hàng năm, doanh nghiệp trích 6 triệu đồng chi phí trích khấu hao tài sản cố định đó vào chi phí kinh doanh.</span></span></span></div>\r\n<p><span xss=removed><span xss=removed><span xss=removed>Tiếp theo, sau 6 năm sử dụng, doanh nghiệp nâng cấp tài sản cố định với tổng chi phí là 20 triệu đồng, thời gian sử dụng được đánh giá lại là 3 năm (tăng 1 năm so với thời gian sử dụng đã đăng ký ban đầu), ngày hoàn thành đưa vào sử dụng là 1/1/2020.<br>Nguyên giá tài sản cố định = 48 triệu đồng   20 triệu đồng = 68 triệu đồng<br>Số khấu hao luỹ kế đã trích = 6 triệu đồng (x) 6 năm = 36 triệu đồng<br>Giá trị còn lại trên sổ kế toán = 68 triệu đồng - 36 triệu đồng = 32 triệu đồng<br>Mức trích khấu hao trung bình hàng năm = 32 triệu đồng : 3 năm = 10.66 triệu đồng/ năm<br>Mức trích khấu hao trung bình hàng tháng = 10.66 triệu đồng : 12 tháng =888.889 đồng/ tháng<br>Từ năm 2020 trở đi, doanh nghiệp trích khấu hao vào chi phí kinh doanh mỗi tháng 888.889 đồng/ tháng đối với máy photo vừa được nâng cấp.</span></span></span></p>', 1488635791, '["5"]', 'http://ketoanbanthoigian.com/images/tin-tuc/cach_tinh_khau_hao_tai_san_co_dinh_theo_duong_thang.png', 'Cách trích khấu hao tài sản cố định theo phương pháp đường thẳng', 'Cách trích khấu hao tài sản cố định theo phương pháp đường thẳng', 'Cách trích khấu hao tài sản cố định theo phương pháp đường thẳngTrích khấu hao tài sản cố định theo phương pháp đường thẳng là phương pháp trích khấu hao theo mức tính ổn định từng năm v', 14, 0),
(121, 'Mức lương hưu và trợ cấp hưởng lương mất sức lao động tăng thêm 8%', '<p>Lương hưu, trợ cấp hàng tháng được điều chỉnh tăng thêm 8%</p>\r\n<p>Ngày 15/07/2016, Bộ lao động thương binh xã hội ban hành Thông tư 23/2016/TT-BLĐTBXH</p>\r\n<p>Theo đó, điều chỉnh mức lương hưu đối với :</p>\r\n<p>Các đối tượng quy định tại Điểm a và Điểm b, Khoản 1, Điều 2, Nghị định số 55/2016/NĐ-CP bắt đầu hưởng lương hưu, trợ cấp hằng tháng trong thời gian từ ngày 01 tháng 01 năm 2015 đến trước ngày 01 tháng 5 năm 2016(a).<br>Các đối tượng quy định tại Điểm a, Điểm b, và Điểm c, Khoản 1, Điều 2, Nghị định số 55/2016/NĐ-CP đang hưởng lương hưu, trợ cấp mất sức lao động, trợ cấp hằng tháng trước ngày 01 tháng 01 năm 2016 và người bắt đầu hưởng lương hưu, trợ cấp hằng tháng trong giai đoạn từ ngày 01 tháng 01 năm 2016 đến ngày 31 tháng 12 năm 2016 có mức lương hưu, trợ cấp dưới 2.000.000 đồng/tháng.(b)<br>……</p>\r\n<p>Cụ thể:</p>\r\n<p>1. Tăng thêm 8% mức lương hưu, trợ cấp hằng tháng đối với các đối tượng quy định tại (a) :</p>\r\n<p>Mức lương hưu, trợ cấp hằng tháng mới = Mức lương hưu, trợ cấp hiện hưởng x 1,08</p>\r\n<p>Thời điểm điều chỉnh tính từ tháng bắt đầu hưởng lương hưu, trợ cấp hằng tháng.</p>\r\n<p>Ví dụ : Ông A, bắt đầu hưởng lương hưu từ ngày 01 tháng 01 năm 2015 với mức lương hưu tại thời điểm tháng 01/2015 là 5.200.000 đồng/tháng.<br>Mức lương hưu mới của ông A sau khi điều chỉnh là:<br>5.200.000 đồng/tháng x 1,08 = 5.616.000 đồng/tháng<br>Thời điểm hưởng mức lương mới nêu trên của ông A được tính từ tháng 01/2015.</p>\r\n<p>2. Điều chỉnh mức hưởng đối với người có mức lương hưu, trợ cấp dưới 2.000.000 đồng/tháng</p>\r\n<p>Đối với các đối tượng quy định tại (b) và các đối tượng sau khi được điều chỉnh mức hưởng theo quy định tại khoản 1<br>– Đối với người có mức lương hưu từ 1.750.000 đồng/tháng trở xuống:<br>Mức lương hưu sau điều chỉnh = Mức lương hưu trước điều chỉnh   250.000 đồng/tháng</p>\r\n<p>– Đối với người có mức lương hưu từ trên 1.750.000 đồng/tháng đến dưới 2.000.000 đồng/tháng:<br>Mức lương hưu sau điều chỉnh = 2.000.000 đồng/tháng</p>\r\n<p>– Đối với người có mức trợ cấp mất sức lao động, trợ cấp hằng tháng từ 1.850.000 đồng/tháng trở xuống:</p>\r\n<p>Mức trợ cấp mất sức lao động, trợ cấp hằng tháng sau điều chỉnh = Mức trợ cấp mất sức lao động, trợ cấp hằng tháng trước điều chỉnh   150.000 đồng/tháng</p>\r\n<p>– Đối với người có mức trợ cấp mất sức lao động, trợ cấp hằng tháng từ trên 1.850.000 đồng/tháng đến dưới 2.000.000 đồng/tháng:<br>Mức trợ cấp mất sức lao động, trợ cấphằng tháng sau điều chỉnh = 2.000.000 đồng/tháng</p>\r\n<p>Thời điểm điều chỉnh tính từ tháng 01 năm 2016 đối với người đang hưởng lương hưu, trợ cấp mất sức lao động, trợ cấp hằng tháng trước ngày 01 tháng 01 năm 2016 và tính từ tháng bắt đầu hưởng lương hưu, trợ cấp hằng tháng đối với người bắt đầu hưởng trong giai đoạn từ ngày 01 tháng 01 năm 2016 đến ngày 31 tháng 12 năm 2016.</p>\r\n<p>Ví dụ 1 : Ông C, bắt đầu hưởng lương hưu từ ngày 01 tháng 01 năm 2016 với mức lương hưu tại thời điểm tháng 01/2016 là 1.600.000 đồng/tháng.<br>– Ông C thuộc đối tượng điều chỉnh tăng 8% theo quy định tại Điều 2 Thông tư này, mức lương hưu của ông C sau khi điều chỉnh tăng 8% là:<br>1.600.000 đồng/tháng x 1,08 = 1.728.000 đồng/tháng<br>– Do mức lương hưu của ông C thấp hơn 1.750.000 đồng/tháng, nên ông C thuộc đối tượng được tiếp tục điều chỉnh tăng thêm 250.000 đồng/tháng theo quy định tại Điểm a Khoản 1 Điều 3 Thông tư này. Mức lương hưu của ông C sau khi điều chỉnh là:<br>1.728.000 đồng/tháng   250.000 đồng/tháng = 1.978.000 đồng/tháng<br>Thời điểm hưởng mức lương nêu trên của ông C được tính từ tháng 01/2016.</p>\r\n<p>Ví dụ 2: Bà D, hưởng trợ cấp mất sức lao động hằng tháng với mức hưởng tại thời điểm tháng 12/2015 là 1.860.000 đồng. Do mức trợ cấp mất sức lao động của bà D nằm trong khoảng từ trên 1.850.000 đồng/tháng đến dưới 2.000.000 đồng/tháng, nên bà D thuộc đối tượng được điều chỉnh mức trợ cấp theo quy định tại Điểm d Khoản 1 Điều 3 Thông tư này.<br>Mức trợ cấp mất sức lao động của bà D sau khi điều chỉnh là 2.000.000 đồng/tháng. Thời điểm hưởng từ tháng 01/2016.</p>\r\n<p>Thông tư này có hiệu lực thi hành kể từ ngày 01 tháng 9 năm 2016.</p>\r\n<p> <div class="moduletablettlienhe">\n<div class="customttlienhe">\n<p><strong>Mọi thông tin xin liên hệ</strong></p>\r\n<p><strong>Dịch Vụ Kế Toán Tại Nhà</strong></p>\r\n<table border="0" xss=removed>\r\n<tbody>\r\n<tr>\r\n<td xss=removed valign="top">Kế toán</td>\r\n<td xss=removed valign="top">:</td>\r\n<td>Đỗ Hằng</td>\r\n</tr>\r\n<tr>\r\n<td>Địa chỉ</td>\r\n<td>:</td>\r\n<td>56 Đường D2, Phường 25, Quận Bình Thạnh, Tp Hồ Chí Minh</td>\r\n</tr>\r\n<tr>\r\n<td>Di động</td>\r\n<td>:</td>\r\n<td>0937. 31 91 94 - 0965.71.48.78</td>\r\n</tr>\r\n<tr>\r\n<td>Email</td>\r\n<td>:</td>\r\n<td><a href="mailto:dvkthang@yahoo.com">dvkthang@yahoo.com</a></td>\r\n</tr>\r\n<tr>\r\n<td>Website</td>\r\n<td>:</td>\r\n<td><a href="http://ketoanbanthoigian.com">ketoanbanthoigian.com</a></td>\r\n</tr>\r\n</tbody>\r\n</table></div>\n</div>\n</p>', 1488635792, '["5"]', 'http://ketoanbanthoigian.com/images/hinh-anh/longghep16-6159-1467186815.jpg', 'Mức lương hưu và trợ cấp hưởng lương mất sức lao động tăng thêm 8%', 'Mức lương hưu và trợ cấp hưởng lương mất sức lao động tăng thêm 8%', 'Mức lương hưu và trợ cấp hưởng lương mất sức lao động tăng thêm 8%Lương hưu, trợ cấp hàng tháng được điều chỉnh tăng thêm 8%\r\nNgày 15/07/2016, Bộ lao động thương binh xã hội ban hành Thông tư ', 14, 0),
(122, 'Cần khai báo mã số thuế người phụ thuộc từ 15 tuổi trở lên', '<p>Cần phải khai báo Mã số thuế người phụ thuộc đối với con trên 15 tuổi và con học trình độ sau đại học</p>\r\n<p>1. Cấp MST người phụ thuộc là con trên 15 tuổi<br>Căn cứ: <br>– Điểm g, Khoản 1, Điều 9, Thông tư 111/2013/TT-BTC<br>– Điều 3, Điều 4, Văn bản hợp nhất 03/VBHN-BCA<br>Theo đó:<br>– Công dân Việt Nam từ đủ 14 tuổi trở lên, đang cư trú trên lãnh thổ Việt Nam có nghĩa vụ đến cơ quan Công an nơi đăng ký hộ khẩu thường trú làm thủ tục cấp CMND theo quy định.<br>Do đó, để tránh kê khai trùng thì hồ sơ chứng minh đối với người phụ thuộc là con trên 15 tuổi bắt buộc phải có bản chụp CMND.<br>– Trường hợp người phụ thuộc thuộc diện tạm thời chưa được cấp CMND theo quy định của cơ quan công an(điều 4, Văn bản hợp nhất 03/VBHN-BCA) nên chưa được cấp MST NPT thì trong thời gian chưa được cấp vẫn được tạm tính giảm trừ gia cảnh cho người phụ thuộc theo thực tế.<br>Trường hợp những người phụ thuộc này khỏi bệnh, hết thời hạn tạm giam, thời hạn thi hành án,… và được cơ quan công an cấp CMND thì người nộp thuế phải thực hiện bổ sung thông tin đăng ký và hoàn thiện hồ sơ theo quy định để được cấp MST NPT.</p>\r\n<p>2. Về việc giảm trừ người phụ thuộc là con đang học tại các trường đại học với trình độ sau đại học (thạc sỹ)</p>\r\n<p>Căn cứ: Điểm d, khoản 1, Điều 9, Thông tư 111/2013/TT-BTC<br>Theo đó:<br>– Trường hợp người phụ thuộc là con đang theo học tại bậc đại học, cao đẳng, trung học chuyên nghiệp, dạy nghề, kể cả con từ 18 tuổi trở lên đang học bậc học phổ thông(tính cả trong thời gian chờ kết quả thi đại học từ tháng 06 đến tháng 09 năm lớp 12) không có thu nhập hoặc có thu nhập bình quân tháng trong năm từ tất cả các nguồn thu nhập không vượt quá 1.000.000 đồng thì được tính là người phụ thuộc của người nộp thuế.<br>– Trường hợp người phụ thuộc là con đang học trình độ thạc sỹ tại các trường đại học thì không thuộc diện được tính là người phụ thuộc của người nộp thuế</p>\r\n<p>Nguồn tham khảo: Công văn 3420/TCT-TNCN, ngày 01/08/2016</p>\r\n<p> <div class="moduletablettlienhe">\n<div class="customttlienhe">\n<p><strong>Mọi thông tin xin liên hệ</strong></p>\r\n<p><strong>Dịch Vụ Kế Toán Tại Nhà</strong></p>\r\n<table border="0" xss=removed>\r\n<tbody>\r\n<tr>\r\n<td xss=removed valign="top">Kế toán</td>\r\n<td xss=removed valign="top">:</td>\r\n<td>Đỗ Hằng</td>\r\n</tr>\r\n<tr>\r\n<td>Địa chỉ</td>\r\n<td>:</td>\r\n<td>56 Đường D2, Phường 25, Quận Bình Thạnh, Tp Hồ Chí Minh</td>\r\n</tr>\r\n<tr>\r\n<td>Di động</td>\r\n<td>:</td>\r\n<td>0937. 31 91 94 - 0965.71.48.78</td>\r\n</tr>\r\n<tr>\r\n<td>Email</td>\r\n<td>:</td>\r\n<td><a href="mailto:dvkthang@yahoo.com">dvkthang@yahoo.com</a></td>\r\n</tr>\r\n<tr>\r\n<td>Website</td>\r\n<td>:</td>\r\n<td><a href="http://ketoanbanthoigian.com">ketoanbanthoigian.com</a></td>\r\n</tr>\r\n</tbody>\r\n</table></div>\n</div>\n</p>\r\n<p> </p>', 1488635794, '["5"]', 'http://ketoanbanthoigian.com/images/hinh-anh/siet-cho-vay-ngoai-te-ngan-hang-va-doanh-nghiep_103979.jpg', 'Cần khai báo mã số thuế người phụ thuộc từ 15 tuổi trở lên', 'Cần khai báo mã số thuế người phụ thuộc từ 15 tuổi trở lên', 'Cần khai báo mã số thuế người phụ thuộc từ 15 tuổi trở lênCần phải khai báo Mã số thuế người phụ thuộc đối với con trên 15 tuổi và con học trình độ sau đại học\r\n1. Cấp MST ngườ', 14, 0),
(123, 'Cách tính thuế TNDN cho doanh nghiệp có cơ sở sản xuất hạch toán phụ thuộc', '<p>Xác định tỷ lệ phân bổ thuế TNDN cho doanh nghiệp có cơ sở sản xuất hạch toán phụ thuộc<br><span xss=removed></span></p>\r\n<p><span xss=removed>Căn cứ:</span></p>\r\n<p>– Điều 12, Điều 13, Thông tư 78/2014/TT-BTC<br>– Điều 16, Thông tư 151/2014/TT-BTC</p>\r\n<p>Theo đó:</p>\r\n<p>Trường hợp doanh nghiệp có cơ sở sản xuất(chi nhánh) hạch toán phụ thuộc hoạt động tại địa bàn tỉnh, thành phố trực thuộc trung ương khác với địa bàn nơi doanh nghiệp đóng trụ sở chính thì doanh nghiệp có trách nhiệm khai thuế, nộp thuế thu nhập doanh nghiệp đối với số thuế thu nhập phát sinh tại trụ sở chính và tại chi nhánh hạch toán phụ thuộc. Cách xác định tỉ lệ phân bổ chi phí thực hiện theo hướng dẫn tại Điều 13, Thông tư 78/2014/TT-BTC, cụ thể:</p>\r\n<p>Tỷ lệ chi phí của cơ sở sản xuất hạch toán phụ thuộc = Tổng chi phí của cơ sở sản xuất hạch toán phụ thuộc/Tổng chi phí doanh nghiệp</p>\r\n<p>Số liệu để xác định tỷ lệ chi phí được căn cứ vào số liệu quyết toán thuế thu nhập của doanh nghiệp năm trước liền kề năm tính thuế do doanh nghiệp tự xác định để làm căn cứ xác định số thuế phải nộp và được sử dụng để kê khai, nộp thuế thu nhập doanh nghiệp cho các năm sau.</p>\r\n<p>Nguồn tham khảo: Công văn 3251/TCT-CS, ngày 20/07/2016</p>\r\n<p> <div class="moduletablettlienhe">\n<div class="customttlienhe">\n<p><strong>Mọi thông tin xin liên hệ</strong></p>\r\n<p><strong>Dịch Vụ Kế Toán Tại Nhà</strong></p>\r\n<table border="0" xss=removed>\r\n<tbody>\r\n<tr>\r\n<td xss=removed valign="top">Kế toán</td>\r\n<td xss=removed valign="top">:</td>\r\n<td>Đỗ Hằng</td>\r\n</tr>\r\n<tr>\r\n<td>Địa chỉ</td>\r\n<td>:</td>\r\n<td>56 Đường D2, Phường 25, Quận Bình Thạnh, Tp Hồ Chí Minh</td>\r\n</tr>\r\n<tr>\r\n<td>Di động</td>\r\n<td>:</td>\r\n<td>0937. 31 91 94 - 0965.71.48.78</td>\r\n</tr>\r\n<tr>\r\n<td>Email</td>\r\n<td>:</td>\r\n<td><a href="mailto:dvkthang@yahoo.com">dvkthang@yahoo.com</a></td>\r\n</tr>\r\n<tr>\r\n<td>Website</td>\r\n<td>:</td>\r\n<td><a href="http://ketoanbanthoigian.com">ketoanbanthoigian.com</a></td>\r\n</tr>\r\n</tbody>\r\n</table></div>\n</div>\n</p>', 1488635795, '["5"]', 'http://ketoanbanthoigian.com/images/hinh-anh/cua-hoi-mon.jpg', 'Cách tính thuế TNDN cho doanh nghiệp có cơ sở sản xuất hạch toán phụ thuộc', 'Cách tính thuế TNDN cho doanh nghiệp có cơ sở sản xuất hạch toán phụ thuộc', 'Cách tính thuế TNDN cho doanh nghiệp có cơ sở sản xuất hạch toán phụ thuộcXác định tỷ lệ phân bổ thuế TNDN cho doanh nghiệp có cơ sở sản xuất hạch toán phụ thuộc\r\nCăn cứ:\r\n– Điều 12, Điều 13, T', 14, 0);
